import './InferenceUI.css';
import {
    Container, Col, Row,
    Form, FormGroup, Label, Input,
    Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';
import React from 'react';

function generateCard(props){
    const fname = props.file.name;
    const img = URL.createObjectURL(props.file)
    const rand_numbers = Array.from(Array(4)).map(x=>Math.random());
    const sum = rand_numbers.reduce(function(a, b){return a + b;}, 0);
    const scores = rand_numbers.map(function(x){return Math.round(x*100/sum)});
    return(
        <Col sm="0" md="3">
            <Card>
                <CardImg top width="100%" src={img}/>
                <CardBody>
                    <CardTitle>{fname}</CardTitle>
                    <CardText>
                        <ul>
                            <li>Miringoesclerosis: {scores[0]}%</li>
                            <li>Otitis Media Crónica: {scores[1]}%</li>
                            <li>Tapón de Cerumen: {scores[2]}%</li>
                            <li>Normal: {scores[3]}%</li>
                        </ul>
                    </CardText>
                </CardBody>
            </Card>
        </Col>
    );
}
class InferenceUI extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            files: []
        }
        this.fileSelectedHandler = this.fileSelectedHandler.bind(this);
    }



    fileSelectedHandler(event){
        console.log([...this.state.files, ...event.target.files])
        this.setState({
            files: [...this.state.files, ...event.target.files]
        });
    }

    render() {
        return (
            <Container fluid id="fileInput">
                <Row>
                    <Col sm="0" md="4">
                    </Col>
                    <Col sm="12" md="4">
                        <Form  className="center">
                            <h1>Inferencia</h1>
                            <FormGroup>
                                <Label for="fileSelection">
                                    <span id="uploadBtn">
                                        Selecciona una imagen
                                    </span>
                                </Label>
                                <Input type="file" name="file" id="fileSelection" onChange={this.fileSelectedHandler}/>
                            </FormGroup>
                        </Form>
                    </Col>
                    <Col sm="0" md="4">
                    </Col>
                </Row>
                <Row>
                    {this.state.files && this.state.files.map((file) => generateCard({file:file}))}
                </Row>
            </Container>
        );
    }
}
export default InferenceUI;
