import './InferenceUI.css'
import React from 'react';
import {Button, Alert} from 'reactstrap';

class WebcamStream extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isVideoLoading: true
    };

    this.videoTag = React.createRef();
    this.canvasPreview = React.createRef();
		this.selectVideoSrc = React.createRef();
    this.tick = this.tick.bind(this);

    this.mirrorCount = 0;
    this.fullScreenCount = 0;

    this.mediaRecorder = null;
    this.recordedChunks= [];
	  this.videoRecorded = null;

	}
  componentDidMount() {
    this.setStream();
  }
  setStream(){
    navigator.mediaDevices
		.enumerateDevices()
		.then( deviceInfos => {
				for (let i = 0; i !== deviceInfos.length; ++i) {
					const deviceInfo = deviceInfos[i];
          console.log(deviceInfos[i]);
					const option = document.createElement('option');
					option.value = deviceInfo.deviceId;
					if (deviceInfo.kind === 'videoinput') {
						option.text = deviceInfo.label || 'camera ' +
							(this.selectVideoSrc.current.length + 1);
						this.selectVideoSrc.current.appendChild(option);
					} 
				}
		})				 
		.then(navigator.mediaDevices
      .getUserMedia({ video: {deviceId: this.selectVideoSrc.current.value ? {exact: this.selectVideoSrc.current.value} : undefined, facingMode: "enviroment"}})
      .then(stream => {
        this.videoTag.current.srcObject = stream;
        requestAnimationFrame(this.tick);
        window.stream = stream;
        var options = { mimeType: "video/webm;codecs=vp8" };
        this.mediaRecorder = new MediaRecorder(stream, options);
      })
		);

  }

  tick() {
    const video = this.videoTag.current;

    const checkVideoState = setInterval(() => {
      if (video.readyState === video.HAVE_ENOUGH_DATA) {
        clearInterval(checkVideoState);

        this.setState({ isVideoLoading: false });

          const canvasPreviewElement = this.canvasPreview.current;
          const canvasPreview = canvasPreviewElement.getContext("2d");

          canvasPreviewElement.height = video.videoHeight;
          canvasPreviewElement.width = video.videoWidth;
          canvasPreview.drawImage(
            video,
            0,
            0,
            canvasPreviewElement.width,
            canvasPreviewElement.height
          );
					if(this.props.currentState.almostStreaming){
						canvasPreview.font = "200px Arial"
						countdown(canvasPreview,this);
					}
           requestAnimationFrame(this.tick);

      }
    }, 50);
  }

  startRecording(){
      this.mediaRecorder.ondataavailable = handleDataAvailable(this);
      this.mediaRecorder.start();
      setInterval(()=>{this.props.getVideoThumbnail(this.canvasPreview.current.toDataURL("image/jpeg",1));},300);

  }
	stopRecording(){
		this.mediaRecorder.stop();
	}

	makeVideo() {
		var blob = new Blob(this.recordedChunks, {
			type: "video/webm"
		});
		this.videoRecorded = URL.createObjectURL(blob);
	}

	downloadVideo(){
		var aux = document.createElement("a");
		document.body.appendChild(aux);
		aux.style = "display: none";
		aux.href = this.videoRecorded;
		aux.download = "test.webm";
		aux.click();
		this.recordedChunks = [];
	}
  render() {
    const { isVideoLoading } = this.state;
    let classNameToRender = (this.props.currentState.fullScreen)? "FullScreen":"RegularScreen";
    return (
      <div>
        <video
          ref={this.videoTag}
          autoPlay
          id = "video"
          style={{ display: "none"}}/>

        {!isVideoLoading && 
          <div  id="mainDiv" 
                className={"container  mainDiv" + classNameToRender }>
            <div id="canvasDiv">
              {this.props.currentState.showAlert && 
                <AlertOnPreview msg="Recibiendo ImÃ¡genes Borrosas"/>}
              <canvas ref={this.canvasPreview} 
                      id="canvasPreview" 
                      className = {"canvasPreview" + classNameToRender}/>
              <div id="mirror" 
                   className={"overlay"  + classNameToRender +" mirror"+ classNameToRender + " overlay"}
                   onClick={()=>{mirror(this)}}>M</div>
              <div id="fullScreen" 
                   className={"overlay"  + classNameToRender +" fullScreen"+ classNameToRender + " overlay"}
                   onClick={()=>{fullScreen(this)}}>PC</div>
              <div  id="belowCanvas" 
                    className={"belowCanvas" + classNameToRender}
                    style={{position: "absolute", top: "900px"}}>
              </div>
            </div>
          </div>
        }
				<div id="videoSelectDiv" >
					<select ref={this.selectVideoSrc}
									style={{width: 300 }}
									id="videoSelect"
                  onChange={()=>{this.setStream();}}/>
					<Button color='primary' 
                  onClick={()=>{document.getElementById("videoSelectDiv").remove();
																this.props.handler("OnConnection");}}
          >Ok</Button>
				</div>
        {isVideoLoading && <p>Please wait while we load the video stream.</p>}

      </div>
    );
  }
}

async function fullScreen(el){
  let div = document.getElementById("mainDiv");
  let canvas  = document.getElementById("canvasPreview");
  let below = document.getElementById("belowCanvas");
  let screenshots = document.getElementById("screenshots"); 
  let btns = await document.getElementById("buttons");

  //go fullscreen
  if (el.fullScreenCount%2 === 0){
    el.props.changeStateProp({fullScreen: true});
    btns.appendChild(document.getElementById("mirror"));
    btns.appendChild(document.getElementById("fullScreen"));
    div.appendChild(btns);
    below.appendChild(screenshots);

    //style
    canvas.style.height = window.screen.height + "px";
    canvas.style.width = window.screen.height*640/480 + "px";
    canvas.style.left = (window.screen.width - getComputedStyle(canvas).width)/2 + "px";

    screenshots.style.width =  getComputedStyle(canvas).width;
    btns.style.width = getComputedStyle(canvas).width;
    below.style.top = window.screen.height + "px";

    if(div.webkitRequestFullScreen) {
      div.webkitRequestFullScreen(); }
    else {
      div.requestFullscreen();} 
    el.divId = "ButtonsFullScreen";
  }

  //exit fullscreen
  else{
    el.props.changeStateProp({fullScreen: false});
    //detach elements
    div.appendChild(document.getElementById("mirror"));
    div.appendChild(document.getElementById("fullScreen"));
    document.getElementById("ElementsMain").appendChild(btns);
    document.getElementById("ElementsMain").appendChild(screenshots);

    //style
    canvas.style.height =  "480px";
    canvas.style.width=  "640px";
    screenshots.style.width =  "640px";
    document.exitFullscreen();
  }
  el.fullScreenCount++;
}

//For making video recording
var handleDataAvailable = function (el) {
  return function (event) {
		console.log("data-available");
  	if (event.data.size > 0) {
   	  el.recordedChunks.push(event.data);
      el.makeVideo();
  	}
		else {
    	console.log("no data available for recording");
  	}

  };
};

function AlertOnPreview(props){
  return(
    <div id="alert" style={{position: 'absolute', zIndex: 2}}>
      <Alert color="warning">{props.msg}</Alert>
    </div>
  )
}
function mirror(el){
  let canvas = document.querySelector("#canvasPreview");
  if (el.mirrorCount%2 === 0){
    canvas.style.transform = "translate(640,0)";
    canvas.style.transform = "scale(-1,1)";
  }
  else{
    canvas.style.transform = "none";
  }
  el.mirrorCount ++;
}

function countdown(canvas,el){
	let context = canvas;	
	let countdown = el.props.countdownFrom- Math.trunc((-el.props.currentState.timeStartPressed + Date.now())/1000);
  context.beginPath();
  context.arc(320, 240, 130, 0, 2 * Math.PI);
  context.fillStyle = "rgba(255, 255, 255, 0.8)";;
  context.fill();
  context.beginPath();
  context.fillStyle = "black";
  if(countdown >= 10){
    context.fillText(countdown.toString(10),200,315);
  }else if(countdown >= 1){
    context.fillText(countdown.toString(10),270,308,100);
  }

  context.fill();
  if (countdown <  1){
    el.props.currentState.almostStreaming = false;
    el.props.currentState.streaming = true;
		el.props.handler("Streaming");
  }

}
export default WebcamStream;
