import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Card,CardImg, Button,  CardText, Row, Col, Container} from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
//import Screenshots from './Screenshots.js';

class Diagnosis extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
		this.selectVideoSrc = React.createRef();
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevicess();
  }
	render(){
		return(
			<div>
					<SideTabs handlerSuper={()=>{this.props.handler("StopStream")}} 
                 		thumbnail={this.props.thumbnail} 
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		screenshots={this.props.currentState.GallerySrc}
                 		currentState={this.props.currentState}
										superElement={this}
										setStreamFromConfig={this.props.setStreamFromConfig}

                 />
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    screenshots,
    currentState,
		superElement,
		setStreamFromConfig
  } = props;
  const [activeTab, setActiveTab] = useState('1');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }


  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '1' })}
            onClick={() => { toggle('1'); }} >
            Diagnostico
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('2'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('3'); }}>
            Capturas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '4' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('4'); }}>
           Configuración 
          </NavLink>
        </NavItem>
      </Nav>

      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <DiagnosisTab />
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler} screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="3">
          <Gallery images={screenshots} rowHeight="80"/>
        </TabPane>
        <TabPane tabId="4">
					<ConfigTab superElement={superElement} setStreamFromConfig={setStreamFromConfig}/>
        </TabPane>
      </TabContent>
    </div>
  );
}

function ConfigTab(props){
	console.log(props);
  return(
		<div id="videoSelectDiv">
    	<Button color ='primary'
              onClick={()=>{listDevicess();}}
    		      >Refrescar</Button>
		  <select ref={props.superElement.selectVideoSrc}
							style={{width: 300}}
							id="videoSelectConf"/>
			<Button color='primary'
              onClick={()=>{props.setStreamFromConfig.call()}}
      >Ok</Button>
		</div>
  );
}
function listDevicess(){
	var selectVideoSrc = document.getElementById("videoSelectConf");
  navigator.mediaDevices.enumerateDevices()
  .then((deviceInfos) => {
    selectVideoSrc.innerHTML = '';
    let count = 1;
    deviceInfos.forEach( deviceInfo => {
      const option = document.createElement('option');
      option.value = deviceInfo.deviceId;
      if (deviceInfo.kind === 'videoinput') {
        option.text = deviceInfo.label || `Camera ${count++}`;
        selectVideoSrc.appendChild(option);
      }
    });
  });

}

function DiagnosisTab(){
	return(
		<Spinner color="info" style={{position:'absolute',margin:'auto'}} size={'xl'}/>
	);
}

const DownloadTab = (props) => {
  const{
    thumbnail,
    downloadHandler,
    screenshots
  }=props;

  let  screenshotsIsEmpty = (screenshots.length === 0)? true:false;
	return(
		<Container>
			<Row>
      	<Col sm="6">
          <Card body>
            <CardImg top width="100%" src= {thumbnail} alt="thumbnail" />
            <CardText>Grabación del examen.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("video")}}> Descargar Video</Button>
          </Card>
        </Col>
        {!screenshotsIsEmpty &&
        <Col sm="6">
          <Card body>
            <CardImg top width="100%" src= {screenshots[0].src} alt="thumbnail" />
            <CardText>Capturas de Pantalla.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("screenshots")}}>Descargar capturas</Button>
          </Card>
        </Col>
        }
     </Row>
		</Container>
	);
}
export default Diagnosis;
