import './InferenceUI.css';
import {Container, 
    } from 'reactstrap';
import React from 'react';
//import io from 'socket.io-client';
import WebcamStream from './WebcamStream.js'
import Screenshots from './Screenshots.js'
import Diagnosis from './Diagnosis.js'
import ElementsMain from './ElementsMain.js'

import "react-responsive-carousel/lib/styles/carousel.min.css";



class InferenceUI extends React.Component {
    constructor(props){
        super(props);
        this.state = {
      		current: "SelectingSource",

					textMainBtn: "Start",
					showStartBtn: false,
					showCaptureBtn: false,
					showVideoSelect: false,
					showProgressBar: false,
					enabledModalScreenshots: false, //if requested
					showModalDiagnosis: false,
					disableBtnCapture: false,

          streaming: false,
          almostStreaming: false,          

					timeStartPressed: 0,

					triggerCaptureScreenshot: false,
          showAlert: false
        }
			this.childScreenshots = React.createRef();
			this.childWebcamStream = React.createRef();
			this.childElementsMain = React.createRef();
			this.countdownFrom = 1;
      this.alertTimeout = null;
    }

		async handler(nextState){
			await this.setState({current: nextState});
		  console.log("estado en switch: " + this.state.current);
      await this.setState(manageStates(this.state.current));
		}

		captureScreenshot(){
			this.childScreenshots.current.captureScreenshot();
		}

    mirrorPreview(){
      this.childWebcamStream.current.mirror();
    }

    changeProgress(){
      this.childElementsMain.changeProgress();
    }

    render() {
      return (
					<Container id="container">

						<WebcamStream id="WebcamStream" 
                          currentState={this.state} 
                          handler={(nextState)=>this.handler(nextState)} 
                          countdownFrom={this.countdownFrom} 
                          ref={this.childWebcamStream}/>
						<ElementsMain currentState={this.state} 
                          handler={(nextState)=>this.handler(nextState)} 
                          captureScreenshot={()=>this.captureScreenshot()} 
                          fromServer={(signal)=>fromServer(this,signal)} 
                          ref={this.childElementsMain}/> 
						<Screenshots  currentState={this.state} 
                          handler={(nextState)=>this.handler(nextState)} 
                          ref={this.childScreenshots} />
						<Diagnosis    currentState={this.state} 
                          handler={(nextState)=>this.handler(nextState)}/>
				  </Container>

        );

    }
}
function fromServer(el,signal){
  if(signal.localeCompare("blur") === 0){
    if(el.state.showAlert){
      clearTimeout(el.alertTimeout);
      el.alertTimeout = setTimeout(function(){el.setState({showAlert: false})},5000)
    }
    else{
      el.setState({showAlert: true});
      el.alertTimeout = setTimeout(function(){el.setState({showAlert: false})},5000)
    }
  }
  else if(signal.localeCompare("progress") === 0){
    el.changeProgress();
  }

}
function manageStates(state){
	var flags = {};
	switch(state){
		case 'OnConnection':
		  flags={
				  textMainBtn: "Start",
				  showStartBtn: true,
				  showCaptureBtn: true,
				  showProgressBar: false,
				  enabledModalScreenshots: true, //if requested
				  showModalDiagnosis: false,
				  disableBtnCapture: false,

				  streaming: false,
				  almostStreaming: false,          
			  };
			  break;
		  case 'Countdown':
			  flags={
				  textMainBtn: "Stop",
				  showStartBtn: true,
				  showCaptureBtn: true,
				  showProgressBar: true,
				  enabledModalScreenshots: false, //if requested
				  showModalDiagnosis: false,
				  disableBtnCapture: true,

				  streaming: false,
				  almostStreaming: true,          

				  timeStartPressed: Date.now()
				  };
				  break;
		  case 'Streaming':
			  flags={
				  textMainBtn: "Stop",
				  showStartBtn: true,
				  showCaptureBtn: true,
				  showProgressBar: true,
				  enabledModalScreenshots: false, //if requested
				  showModalDiagnosis: false,
				  disableBtnCapture: false,

				  streaming: true,
				  almostStreaming: false,          

			  };
			  break;
		  case 'StopStream':
			  flags={
				  textMainBtn: "Start",
				  showStartBtn: true,
				  showCaptureBtn: true,
				  showProgressBar: true,
				  enabledModalScreenshots: true, //if requested
				  showModalDiagnosis: true,
				  disableBtnCapture: false,

				  streaming: false,
				  almostStreaming: false,          
			  };
			  break;
		  default:
			  console.log('State not defined');

	  }
	return flags;
  }
export default InferenceUI;
