import './SidePanel.css'
import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Card,CardImg, Button,  CardText, Row, Col, Container} from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
import {listDevices} from './functions.js'

class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
      ///hidePanel: false
		}
    this.canvasHeight = 0;
    this.canvasWidth = 0;
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
  saveCanvasDim(w,h){
    this.props.saveCanvasDim(w,h);
//    this.props.changeStateProp({canvasWidth: w});
  //  this.props.changeStateProp({canvasHeight: h});
  }
  getCanvasDim(){
    return this.props.getCanvasDim();
  }
  render(){
		return(
			<div>
        {!this.props.currentState.hideSidePanel && 
			    <div id="sidePanel_Wrapper">
				    <SideTabs
                 		thumbnail={this.props.thumbnail} 
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		screenshots={this.props.currentState.GallerySrc}
                 		currentState={this.props.currentState}
										superElement={this}
            />
          {!this.props.currentState.isVideoLoading &&
            <div id="hideSidePanel" className="ctrlTabs rotate90" onClick={()=>hideSidePanel(this)}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
            </div>}
					</div>}

        {this.props.currentState.hideSidePanel && 
          <div id="showSidePanel" className="ctrlTabs rotate-90" onClick={()=>showSidePanel(this)}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
              </div>
        }
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    screenshots,
    currentState,
		superElement,
  } = props;
  const [activeTab, setActiveTab] = useState('4');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }


  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '1' })}
            onClick={() => { toggle('1'); }} >
            Diagnostico
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('2'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('3'); }}>
            Capturas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '4' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('4'); }}>
            <i class="fas fa-cog"></i>
          </NavLink>
        </NavItem>
      </Nav>
      <div id="TabsBody" style={{overflow: "scroll"}}>
      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <DiagnosisTab />
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler} screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="3">
          <GalleryTab screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="4">
					<ConfigTab superElement={superElement} />
        </TabPane>
      </TabContent>
      </div>
    </div>
  );
}
function GalleryTab(props){
