function hideSidePanel(el){
  el.props.changeStateProp({hideSidePanel: true});
  let canvas = document.getElementById("canvasDiv");
  let btns = document.getElementById("buttons");
  let canvasPreview = document.getElementById("canvasPreview");

  el.canvasHeight = canvas.clientHeight*100/window.innerWidth; //vw
  el.canvasWidth = canvas.clientWidth*100/window.innerWidth; //vw
  document.getElementById("WebcamStream_Wrapper").style.width = "100vw";
  if (87*el.canvasWidth/el.canvasHeight < window.innerWidth){
    canvas.style.height = "87vh";
    canvas.style.width = 87*el.canvasWidth/el.canvasHeight+"vh";
    btns.style.width = 87*el.canvasWidth/el.canvasHeight+"vh";
    btns.style.marginLeft = (100-(canvas.clientWidth*100/window.innerWidth))/2 + "vw";
    btns.style.top = 87 - btns.clientHeight*100/window.innerHeight + "vh";
  }
  else{
    canvas.style.width = "90vw";
    canvas.style.height = 90*el.canvasHeight/el.canvasWidth + "vh";
  }
  canvas.style.marginLeft = (100-(canvas.clientWidth*100/window.innerWidth))/2 + "vw";
  canvasPreview.style.height="100%";
  canvasPreview.style.width="100%";
  btns.style.opacity = 0;
  canvas.addEventListener("mouseover",showButtons, false);
  btns.addEventListener("mouseover",showButtons, false);
  canvas.addEventListener("mouseout",hideButtons,false);



}
function showSidePanel(el){
  el.props.changeStateProp({hideSidePanel: false});
  document.getElementById("WebcamStream_Wrapper").style.width = "55vw";
  let btns = document.getElementById("buttons");
  let canvas = document.getElementById("canvasDiv");
  let canvasPreview = document.getElementById("canvasPreview");
  canvas.style.width = el.canvasWidth+ "vw";
  canvas.style.height = el.canvasHeight + "vw";
  canvasPreview.style.width="100%";
  canvasPreview.style.height="100%";
  canvas.style.marginLeft = "2.5vw";
  canvas.style.marginRight = "2.5vw";
  btns.style.width = "50vw";
  btns.style.marginLeft = "2.5vw";
  btns.style.top = el.canvasHeight + "vw";
  btns.style.opacity = 1;
  canvas.removeEventListener("mouseover",showButtons,false);
	btns.removeEventListener("mouseover",showButtons,false);
	canvas.removeEventListener("mouseout",hideButtons,false);

}
