import { Button, Progress, Modal, ModalHeader, ModalBody, ModalFooter
} from 'reactstrap';
import React, {useState} from 'react';
import {Container, Row, Col } from 'react-bootstrap';
import './InferenceUI.css'

class ElementsMain extends React.Component{
  constructor(props){
      super(props);
      this.state = {
				openNMAlert: false,
        fullScreen: false
      }
      this.progressBarColor = "info"; //reactStrap notation
			this.openNMAlert = false;
  }

  setProgressBarColor(){
    let p =parseInt(this.props.currentState.progress,10);
    if (p < 80){
      this.progressBarColor = "info";
    }
    else if (p < 100){
      this.progressBarColor = "success";
    }
    else{
      this.progressBarColor = "danger";
    }
    return this.progressBarColor;
  }


  render(){
    let classToRender = this.state.fullScreen? "fullScreen":"default";
    return(
      <div id="mainElements" class="fullScreen">
          {this.props.currentState.showStartBtn && 
            <Button  
                    onClick={() => startStopFunction(this)}
                    className="default">
              {this.props.currentState.textMainBtn}</Button>}
        {this.props.currentState.showCaptureBtn && 
          <Button color="primary" 
                  disabled={this.props.currentState.disableBtnCapture}  
                  onClick={() => this.props.captureScreenshot()}>
            {"Capture Screenshot"}</Button>}
        {this.props.currentState.showProgressBar && 
          <Progress value={this.props.currentState.progress} 
                    color={this.setProgressBarColor()}/>}
			  {this.state.openNMAlert && 
					<ModalAlertNM parent={this}/>}

        <div id="froServer">
        <h4>From Server</h4>
			  <Button color= "secondary" 
								onClick={()=>this.props.fromServer("blur")}>
								{"Blur"}</Button>
			  <Button color= "secondary" 
								onClick={()=>this.props.fromServer("NM")}>
								{"NM"}</Button>
        <input  type="range" 
								min="0" 
								max="100" 
								id="range" 
								onChange={(e)=>{this.props.fromServer("progress",e.target.value);}}/>
			  <Button color= "secondary"
                onClick={()=>{
                  let el = document.getElementById("previewDiv");
                  let canvas  = document.getElementById("canvasPreview");
                  let below = document.getElementById("belowCanvas");
                  el.style.overflow = "auto";
                  canvas.style.height = window.screen.height + "px";
                  below.style.top =  window.screen.height + "px";
                  console.log(canvas.height);
                  if(el.webkitRequestFullScreen) {
                    el.webkitRequestFullScreen();

                  }
                  else {
                    el.mozRequestFullScreen();
                  } 
                  this.setState({fullScreen: true});
                }}>

                {"FP"} 
        </Button>
        </div>
      </div>
    );
  }
}
function startStopFunction(btn){
  if (btn.props.currentState.textMainBtn.localeCompare("Start") === 0){
    startFunction(btn);
    return;
  }
  stopFunction(btn);
}

function startFunction(btn){
  console.log(btn.props)
  btn.props.handler("Countdown");
  btn.props.currentState.almostStreaming = true;
  setTimeout(function(){
   btn.props.startRecording();
      },btn.props.countdownFrom*1000 + 600);
  }


function stopFunction(btn){
   btn.props.handler("StopStream");
   btn.props.stopRecording();
}


const ModalAlertNM = (props) => {
  const {
    className,
		parent
  } = props;

  const [modal, setModal] = useState(true);

  const toggle = () => {
					//setModal(!modal);
					parent.setState({openNMAlert: false});
					}

  return (
    <div>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Cancelar</ModalHeader>
        <ModalBody>
					Se ha cortado porque no se detecta la membrana
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>Ok</Button>{' '}
        </ModalFooter>
      </Modal>
    </div>
  );
}
export default ElementsMain;
