export function listDevices(){
  console.log("ld");
	var selectVideoSrc = document.getElementById("videoSelectConf");
  if(selectVideoSrc){
  navigator.mediaDevices.enumerateDevices()
  .then((deviceInfos) => {
    selectVideoSrc.innerHTML = '';
    let count = 1;
    deviceInfos.forEach( deviceInfo => {
      const option = document.createElement('option');
      option.value = deviceInfo.deviceId;
      if (deviceInfo.kind === 'videoinput') {
        option.text = deviceInfo.label || `Camera ${count++}`;
        selectVideoSrc.appendChild(option);
      }
    });
  });
  }

}

export function setGallery2(props){
//  console.log(props.currentState.GallerySrc);

  if(props.currentState.GalleryIndxShowing.length === 0){
    props.setGalleryIndxShowing(props.currentState.GallerySrc[0]);
    document.getElementById('img1').src = props.currentState.GallerySrc[0].src;
  }
  else if(props.currentState.GalleryIndxShowing.length === 1){
    props.setGalleryIndxShowing(props.currentState.GallerySrc[1]);
    document.getElementById('img2').src = props.currentState.GallerySrc[1].src;
  }
  else if(props.currentState.GalleryIndxShowing.length === 2){
    props.setGalleryIndxShowing(props.currentState.GallerySrc[2]);
    document.getElementById('img3').src = props.currentState.GallerySrc[2].src;
  }
}
export default {listDevices, setGallery2};
