function clickOnImage(number) {
  for (let i = 1; i <= 3; i++) {
    let name = 'img'.concat(i);

    if (i !== number) {
      document.getElementById(name).style.borderStyle = 'none';
      document.getElementById(name).style.top = '6vw';
      document.getElementById(name).style.width = '10vw';
      document.getElementById(name).style.left = 3.4 + (i - 1) * 11 + 'vw';
    } else {
      document.getElementById(name).style.border = '0.5vw solid #021a40';
      document.getElementById(name).style.top = '5.5vw';
      document.getElementById(name).style.width = '11vw';
      document.getElementById(name).style.left = 3.4 + (i - 1) * 11 - 0.5 + 'vw';
