import './InferenceUI.css';
import {Container, Button, Modal, ModalHeader, ModalBody, ModalFooter, Progress
    } from 'reactstrap';
import React, {useState}from 'react';
//import io from 'socket.io-client';

import "react-responsive-carousel/lib/styles/carousel.min.css";

var almostStreaming = false;
var timeStartPressed;
var streaming;
var modalOn = false;

var CSHiddenBtn;
var VSHiddenSelect;
class WebcamStream extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isVideoLoading: true
    };

    this.videoTag = React.createRef();
    this.canvasPreview = React.createRef();
		this.selectVideoSrc = React.createRef();
    this.tick = this.tick.bind(this);
  }
  componentDidMount() {
    navigator.mediaDevices
		.enumerateDevices()
		.then( deviceInfos => {
				for (let i = 0; i !== deviceInfos.length; ++i) {
					const deviceInfo = deviceInfos[i];
					const option = document.createElement('option');
					option.value = deviceInfo.deviceId;
					if (deviceInfo.kind === 'videoinput') {
						option.text = deviceInfo.label || 'camera ' +
							(this.selectVideoSrc.current.length + 1);
						this.selectVideoSrc.current.appendChild(option);
					} 
				}
		})				 
		.then(navigator.mediaDevices

      .getUserMedia({ video: { facingMode: "environment" } })
      .then(stream => {
        this.videoTag.current.srcObject = stream;
        requestAnimationFrame(this.tick);
        window.stream = stream;
      })
		);
  }


  tick() {
    const video = this.videoTag.current;

    const checkVideoState = setInterval(() => {
      if (video.readyState === video.HAVE_ENOUGH_DATA) {
        clearInterval(checkVideoState);

        this.setState({ isVideoLoading: false });

          const canvasPreviewElement = this.canvasPreview.current;
          const canvasPreview = canvasPreviewElement.getContext("2d");

          canvasPreviewElement.height = video.videoHeight;
          canvasPreviewElement.width = video.videoWidth;
          canvasPreview.drawImage(
            video,
            0,
            0,
            canvasPreviewElement.width,
            canvasPreviewElement.height
          );
					if(almostStreaming){
						canvasPreview.font = "200px Arial"
						countdown(5,canvasPreview,this);
					}
        requestAnimationFrame(this.tick);
      }
    }, 50);
  }

  render() {
    const { isVideoLoading } = this.state;

    return (
      <div>
        <video
          ref={this.videoTag}
          width="400"
          height="400"
          autoPlay
          style={{ display: "none" }}
        />

        {!isVideoLoading && <canvas ref={this.canvasPreview} id="canvasPreview" />}

        {isVideoLoading && <p>Please wait while we load the video stream.</p>}
				<div id="videoSelectDiv">
					<select ref={this.selectVideoSrc}
									style={{width: 300 }}
									id="videoSelect"

					/>
					<Button color='primary' onClick={()=>{
										console.log(this.props);
										document.getElementById("videoSelectDiv").remove();

										this.props.handler("OnConnection");
					}}>Ok</Button>
				</div>

      </div>
    );
  }
}

function countdown(seconds,canvas,el){

	let context = canvas;	
	let countdown = seconds - Math.trunc((-timeStartPressed + Date.now())/1000);
  context.beginPath();
  context.arc(320, 240, 130, 0, 2 * Math.PI);
  context.fillStyle = "rgba(255, 255, 255, 0.8)";;
  context.fill();
  context.beginPath();
  context.fillStyle = "black";
  if(countdown >= 10){
    context.fillText(countdown.toString(10),200,315);
  }else if(countdown >= 1){
    context.fillText(countdown.toString(10),270,308,100);
  }

  context.fill();
  if (countdown <  1){
    almostStreaming = false;
    streaming = true;
		console.log(el.props);

		el.props.handler("Streaming");
  }

}
var slidesData = [];
var Carousel = require('react-responsive-carousel').Carousel;
const ModalScreenshots = (props) => {
  const {
    buttonLabel,
    className,
		parent

  } = props;
  const [modal, setModal] = useState(true);

  const toggle = () => {
    setModal(!modal); 
		console.log("as");
		props.parent.handler;
    modalOn = false;
  }

  return (
    <div>
      <Modal isOpen={modal} toggle={toggle} className={className} size={'xl'}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
        <Carousel showArrows={true} selectedItem={0} >
          {slidesData.map((slide) =>
            <div key={0}>
              <img className="slick-slide-image" src={slide.src} alt='Error' />
            </div>
          )}
        </Carousel>
        </ModalBody>
        <ModalFooter>
        </ModalFooter>
      </Modal>
    </div>
  );
}
class Screenshots extends React.Component{
	state = {
		lastScreenshotID: 0,
    galleryImages: [], 
		modalOn: false
	}

	captureScreenshot = async () => {
		var canvas = document.querySelector("#canvasPreview");
		var imgAsDataURL = canvas.toDataURL("image/jpeg",1);
    let newSlidesData = [{src: imgAsDataURL}];
		slidesData = slidesData.concat(newSlidesData);
		await this.setState({lastScreenshotID: this.state.lastScreenshotID+1});
		let btnsCarousel = document.getElementsByClassName("control-arrow");
		document.querySelector(".carousel-slider").style.display = 'none';
	  let thumbs = document.getElementsByClassName("thumb");
		thumbs[thumbs.length-1].onclick = async() => {
			if(this.props.currentState.showModalScreenshots){
				this.props.handler("Screenshots")
				this.modalOn = true;
			}
		};	
		try{
			btnsCarousel[2].style.backgroundColor= 'black';
			btnsCarousel[3].style.backgroundColor= 'black';
		}
		catch{

		}

}
	componentDidMount() {
    CSHiddenBtn = document.querySelector("#BtnCaptureScreenshot");
	}
	render(){
    return(
			<div>
				<button color="primary" id="BtnCaptureScreenshot" onClick={() => this.captureScreenshot()} style={{display: 'none'}}>Capture Screenshot</button>
				{this.modalOn && <ModalScreenshots parent={this} handler={()=>{console.log("asdsdds");}}/>}
        <Carousel showArrows={true} selectedItem={this.state.lastScreenshotID} >
          {slidesData.map((slide) =>
            <div key={this.state.lastScreenshotID}>
              <img className="slick-slide-image" src={slide.src} alt='' />
            </div>
          )}
        </Carousel>
			</div>
    );
	}

}
function startStopFunction(btn){
  if (btn.props.currentState.textMainBtn.localeCompare("Start") === 0){
		btn.props.handler("Countdown");
    startFunction(btn);
    return;
  }
  //stopFunction();
}
function startFunction(btn){

	almostStreaming = true;
	timeStartPressed = Date.now();

}
class ComponentsMain extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
	}


	render(){
		return(
			<div>
			{this.props.currentState.showStartBtn && <Button color="primary" onClick={() => startStopFunction(this)}>{this.props.currentState.textMainBtn}</Button>}
			{this.props.currentState.showCaptureBtn && <Button color="primary" disabled={this.props.currentState.disableBtnCapture}  onClick={() => CSHiddenBtn.click()}>{"Capture Screenshot"}</Button>}
			{this.props.currentState.showProgressBar && <Progress/>}
			</div>
		);
	}
}
class InferenceUI extends React.Component {
    constructor(props){
        super(props);
        this.state = {
      		current: "SelectingSource",
					textMainBtn: "Start",
					showStartBtn: false,
					showCaptureBtn: false,
					showVideoSelect: false,
					showProgressBar: false,
					showModalScreenshots: false, //if requested
					showModalDiagnosis: false,
					disableBtnCapture: false,
        }
    }

		async handler(nextState){
			await this.setState({current: nextState});
			this.showWeas();
		} 

  	showWeas(){
		console.log("estado en switch: ");
		console.log(this.state.current);

    	switch(this.state.current){
      	case 'OnConnection':
        	this.setState({
          	textMainBtn: "Start",
						showStartBtn: true,
						showCaptureBtn: true,
						showProgressBar: false,
						showModalScreenshots: true, 
						showModalDiagnosis: false,
						disableBtnCapture: false
					});
					break;
				case 'Countdown':
					this.setState({
						textMainBtn: "Stop",
						disableBtnCapture: true,
						showModalScreenshots: false 
					});
					break;
				case 'Streaming':
					this.setState({
						textMainBtn: "Stop",
						showProgressBar: true,
						showModalScreenshots: false,
						showModalScreenshots: false ,
						disableBtnCapture: false 
					});
					break;
				case 'StopStream':
					this.setState({
						textMainBtn: "Start",
						showModalDiagnosis: true,
						showModalScreenshots: true 
					});
					break;
				case 'Screenshots':
					this.setState({
						showModalScreenshots: true,
					});
					break;
				default:
					console.log('State not defined');

			}
		}
    render() {
      return (
					<Container id="container">
						<WebcamStream id="WebcamStream" handler={(nextState)=>this.handler(nextState)}/>
						<ComponentsMain currentState={this.state} handler={(nextState)=>this.handler(nextState)}/> 
						<Screenshots currentState={this.state} handler={(nextState)=>this.handler(nextState)}/>
				</Container>

        );

    }
}

export default InferenceUI;
