import './InferenceUI.css';
import {Container
    } from 'reactstrap';
   /* Container, Col, Row,
    Form, FormGroup, Label, Input,
    Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';*/
import React, {useRef, useEffect} from 'react';
import io from 'socket.io-client';


const Canvas = props => {
  const canvasRef = useRef(null)
  useEffect(() => {
    const canvas = canvasRef.current
    const context = canvas.getContext('2d')

		canvas.width = 640;
		canvas.height= 480;
		context.width= 640;
		context.height= 480;

    context.fillStyle = '#000000'
    context.fillRect(0, 0, context.canvas.width, context.canvas.height)

		var video = document.querySelector("#asa")
		console.log("aa");
		console.log(video);
		context.drawImage(video,0,0,context.width,context.height);
  }, [])



  return <canvas ref={canvasRef} {...props}/>
}

class WebcamStream extends React.Component {
    constructor(props) {
        super(props);
        this.videoTag = React.createRef()
				this.id = ""
    }

    componentDidMount() {
        // getting access to webcam
       navigator.mediaDevices
            .getUserMedia({video: true})
            .then(stream => this.videoTag.current.srcObject = stream)
            .catch(console.log);
    }

    render() {
        return <video id={this.props.id}
                      ref={this.videoTag}
                      width={this.props.width}
                      height={this.props.height}
                      autoPlay
                      title={this.props.title}></video>
    }
}
class InferenceUI extends React.Component {
    constructor(props){
        super(props);
        this.state = {

        }
    }

    render() {
        return (
					<Container id="container">
						<WebcamStream id="asa"/>
						<Canvas/>
					</Container>
        );

    }
}
export default InferenceUI;
