import './SidePanel.css'
import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Button } from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
import {listDevices, setGallery2, galleryPrev, galleryNext} from './functions.js'

import logoVideo from './logoVideo2_2.png'
import logoImg from './logoImg_2.png'
import logoDownloadAll from './descargar_todo.png'
import logoFlipH from './flipH_2.png'
import logoFlipV from './flipV_2.png'
import logoResetFlip from './reset_image.png'
import logo from './logo.png'
import frame138 from './MaterialDiagnostico/frame138.png'
import frame302 from './MaterialDiagnostico/frame302.png'
import frame1282 from './MaterialDiagnostico/frame1282.png'

class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
    this.canvasHeight = 0;
    this.canvasWidth = 0;
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
  setGallery2(){
    setGallery2(this.props);
  }
  componentDidMount(){
    listDevices();
    document.getElementById("countdownFromInput").value="1";
  }
  saveCanvasDim(w,h){
    this.props.saveCanvasDim(w,h);
  }
  getCanvasDim(){
    return this.props.getCanvasDim();
  }

  galleryCtrl(dir, galleryIndxSh, gallerySrc, newPhoto){
    if(dir.localeCompare('prev') === 0){
      galleryPrev(galleryIndxSh, gallerySrc, newPhoto);
    }
    else if(dir.localeCompare('next') === 0){
      galleryNext(galleryIndxSh, gallerySrc, newPhoto);
    }
  }
  render(){
		return(
			<div>
        {!this.props.currentState.hideSidePanel && 
			    <div id="sidePanel_Wrapper">
				    <SideTabs
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		currentState={this.props.currentState}
                    gallerySrc={this.props.gallerySrc}
                    setGalleryIndxShowing={this.props.setGalleryIndxShowing}
										superElement={this}
            />
          {!this.props.currentState.isVideoLoading  &&  this.props.currentState.renderCanvas &&
            <div id="hideSidePanel" className="ctrlTabs rotate90" onClick={()=>hideSidePanel(this)}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
            </div>}
					</div>}

        {this.props.currentState.hideSidePanel && 
          <div id="showSidePanel" className="ctrlTabs rotate-90" onClick={()=>{showSidePanel(this);console.log(document.getElementById("countdownFromInput"))}}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
              </div>
        }
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    currentState,
    gallerySrc,
		superElement,
    setGalleryIndxShowing
  } = props;
const [activeTab, setActiveTab] = useState(superElement.props.currentState.tabOn);
  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
      superElement.props.setTabOn(tab);
  }

  if(activeTab !== superElement.props.currentState.tabOn){
    setActiveTab(superElement.props.currentState.tabOn);
  }

  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '1' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('1'); }}>
            <i className="fas fa-cog"></i>
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('2'); }}>
            Fotos 
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('3'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '4' })}
            onClick={() => { toggle('4'); }} >
            Diagnóstico
          </NavLink>
        </NavItem>
      </Nav>
      <div id="TabsBody">
      <TabContent activeTab={activeTab} style={{height: "100%"}}>
        <TabPane tabId="1" style={{height: "100%"}}>
					  <ConfigTab superElement={superElement} style={{height: "100%"}}/>
        </TabPane>
        <TabPane tabId="2">
          <GalleryTab gallerySrc={gallerySrc} 
                      galleryIndxSh={currentState.GalleryIndexShowing}
                      setGalleryIndxShowing={setGalleryIndxShowing}
                      />
        </TabPane>
        <TabPane tabId="3">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler}  />
        </TabPane>
        <TabPane tabId="4">
            <DiagnosisTab superElement={superElement} />
        </TabPane>
      </TabContent>
      </div>
    </div>
  );
}
function ConfigTab(props){
  return(
		<div id="configTab">
      <table className = "tableDefault fullSpace">
        <tbody className ="fullSpace">
          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="5" style={{}}>
              <div>Seleccione dispositivo:</div>
            </td>
            <td colSpan="5"> 
  		          <select id="videoSelectConf"/>
            </td>
            <td colSpan="2">
  			        <Button color='secondary'
                        outline
                        onClick={()=>{props.superElement.props.setStreamFromConfig()}}
                        id="selectSrcBtn"
                >Ok</Button>
            </td>
            <td colSpan="1">
      	    <span id="RefreshBtn"
                    onClick={()=>{listDevices();}}
                    >
  				    <i className="fas fa-sync-alt"></i>
  			    </span>

            </td>
          </tr>


          <tr style={{height: "5%"}}/>

          <tr style={{height:"2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="13">
              <div>Orientación de la imagen:</div>
            </td>
          </tr>

          <tr style={{height: "2%"}}/>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorHBtn" onClick={()=>{props.superElement.props.mirror("horizontal")}}>
                <img src={logoFlipH} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar horizontalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorVBtn" onClick={()=>{props.superElement.props.mirror("vertical")}}>
                <img src={logoFlipV} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar verticalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorRstBtn" onClick={()=>{props.superElement.props.mirror("reset")}}>
                <img src={logoResetFlip} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Restablecer</div>
            </td>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr style={{height: "2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="5">
              <div>Cuenta regresiva desde: </div>
            </td>
            <td colSpan="5">
              <input  type="number" id="countdownFromInput" min="1" max="20"/> 
            </td>
          </tr>

          <tr style={{height: "15%"}}> 
          </tr>
        </tbody>
      </table>
		</div>

  );

}

function DiagnosisTab(props){
	return(
      <div className = "fullSpace">
      {!props.superElement.props.currentState.isDiagnosisReady &&

        <table className = "tableDefault fullSpace">
          <tbody className = 'fullSpace'>
            <tr style={{height: "10%"}}/>
            <tr style={{height: "30%"}}>
              <td colSpan="1"/>
              <td colSpan="2">
                <img src={logo} alt="logo" style={{width: "100%"}}/>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "10%"}}>
              <td colSpan="1"/>
              <td colSpan="2" style={{textAlign: "center"}}>
                <div> Procesando... </div>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "10%"}}>
              <td colSpan="1"/>
              <td colSpan="2" style={{textAlign: "center"}}>
  	            <Spinner color="info" style={{margin:'auto'}} size={'xl'}/>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "40%"}}/>
          </tbody>  
        </table>
        }

        {props.superElement.props.currentState.isDiagnosisReady &&
        <table className = "tableDefault fullSpace">
          <tbody>
            <tr className = "fullSpace">

              <td colSpan="3">

                <table class = "tableDefault fullSpace" style={{fontSize: '90%'}}>
                  <tbody>
                    <tr style={{height: '2%'}}/>

                    <tr style={{height: '20%'}}>
                      <td colSpan="1">
                        <img src= {frame138} alt="frame" style={{width: "90%"}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Tímpano normal </div>
                      </td>
                    </tr>

                    <tr style={{height: '10%'}}/>

                    <tr style={{height: '20%'}}>
                      <td colSpan="1">
                        <img src= {frame302} alt="frame" style={{width: "90%"}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Exostosis del conducto </div>
                      </td>
                    </tr>

                    <tr style={{height: '10%'}}/>

                    <tr style={{height: '20%'}}>
                      <td colSpan="1">
                        <img src= {frame1282} alt="frame" style={{width: "90%"}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Cera </div>
                      </td>
                    </tr>

                    <tr style={{height: '18%'}}/>
                  </tbody>
                </table>

              </td>

              <td colSpan="2">
                <table className = "tableDefault fullSpace">
                  <tbody>
                    <tr style = {{height: "25%", position: 'realtive', fontSize: '90%' }}>
                      <div style={{height: '20%', fontWeight: 'bold' }}> Diagnóstico AMIRA:</div>
                      <div class = "AmiraBox" style={{ height: '80%'}} >
                          <ul className = "listNoneItem">
                            <li> Exostosis del conducto </li>
                            <li> Tímpano normal </li>
                            <li> Cera  </li>
                         </ul>
                      </div>
                    </tr>

                    <tr style = {{height: "3%", position: 'realtive' }}/>

                    <tr style = {{height: "25%", position: 'realtive', fontSize: '90%'}}>
                      <div style={{height: '20%',  fontWeight: 'bold'}} > Diagnóstico final:</div>
                      <div style={{height: '80%'}}>
                        <textarea class= 'AmiraBox' cols="20" rows="1" style={{height: "100%", width: '100%', resize: 'none'}} ></textarea>
                      </div>
                    </tr>

                    <tr style = {{height: "3%", position: 'realtive' }}/>

                    <tr style = {{height: "25%", position: 'realtive', fontSize: '90%'}}>
                      <div style={{height: '20%',  fontWeight: 'bold'}} > Observaciones:</div>
                      <div style={{height: '80%'}}>
                        <textarea class= 'AmiraBox' cols="20" rows="1" style={{height: "100%", width: '100%', resize: 'none'}} ></textarea>
                      </div>
                    </tr>


                    <tr style = {{height: "7%"}}/>

                    <tr style= {{height: '22%'}}>
                        <div id='btnMakeReport'>
                          Generar Informe
                        </div>
                    </tr>
                  </tbody>
                </table>
              </td>
            </tr>
          </tbody>
        </table>
         }
      </div>
	);
}

function GalleryTab(props){
  let galleryIndxSh = props.galleryIndxSh;
  let mapKeys = Array.from(props.gallerySrc.keys());
  return(
    <div className = 'fullSpace'>
      <table style={{height: '70vh', width: '39vw', display: 'flex'}}>
        <tbody  style={{height: '70vh', width: '39vw', display: 'flex'}}>
          <tr style={{ height: '25vh', width: '39vw', display: 'flex'}}>
            <td style={{width: '3vw'}}>
              <div id = 'galleryPrev' 
              onClick = {()=>{props.setGalleryIndxShowing(
                             [galleryIndxSh[0], galleryIndxSh[1], 
                              mapKeys[mapKeys.indexOf(galleryIndxSh[0])-1]],
                            [1,2,0], false,'prev')}} />


            </td>
            <td style={{width: '33vw'}}>
              <img src='' id= 'img1' alt='img1' onClick={()=>clickOnImage(1)}/> 
              <img src='' id='img2' alt='img2' onClick={()=>clickOnImage(2) }/>
              <img src='' id='img3' alt='img3' onClick={()=>clickOnImage(3) }/>

            </td>
            <td style={{width: '3vw'}}>
              <div id = 'galleryNext' 
                    onClick = {()=>{props.setGalleryIndxShowing(
                             [galleryIndxSh[1], galleryIndxSh[2], 
                              mapKeys[mapKeys.indexOf(galleryIndxSh[2])+1]],
                            [0,1,2], false,'next')}} />
            </td>
          </tr>
          <tr style={{ height: '45vh'}}>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

function clickOnImage(number){

  for (let i=1; i <= 3; i++){
    let name = 'img'.concat(i);
    console.log(name);
    document.getElementById(name).style.borderStyle = 'none';
  }
    name = 'img'.concat(number);
    console.log(document.getElementById(name));
    document.getElementById(name).style.borderStyle = '2px solid black';
}
const DownloadTab = (props) => {
  const{
    downloadHandler,
    screenshots
  }=props;

  //let  screenshotsIsEmpty = (screenshots.length === 0)? true:false;
  let  screenshotsIsEmpty = false; 
	return(
    <table style={{width:"100%", height: "100%", borderCollapse:"collapse", tableLayout: "fixed"}}>
      <tbody className = 'fullSpace'>
        <tr style={{height: "5%"}}/> 
        <tr style={{height: "10%"}}>
          <td colSpan="2">
            <div id="downloadRec" className="downloadIcon fullSpace" onClick={()=>{downloadHandler("video")}}> 
              <img src={logoVideo} alt="Video" style={{width: "100%"}}/>
            </div>
          </td>
          <td colSpan = "5">
            <div>
            Descargar grabación del examen.
            </div>
          </td>
        </tr>

        <tr style={{height: "5%"}}></tr>
        {!screenshotsIsEmpty &&
        <tr style={{height: "10%"}}>
          <td colSpan="2">
            <div id="downloadScreenshots" className="downloadIcon" onClick={()=>{downloadHandler("screenshots")}}> 
            <img src={logoImg} alt="Video" style={{width: "100%"}}/>
            </div>
          </td>
          <td colSpan = "5">
            <div>
            Descargar fotos tomadas durante el examen.
            </div>
          </td>
        </tr>
        }
        <tr style={{height: "5%"}}></tr>
        {!screenshotsIsEmpty &&
        <tr style={{height: "10%"}}>
          <td colSpan="2">
            <div id="downloadAll" className="downloadIcon" onClick={()=>{downloadHandler("all")}}> 
            <img src={logoDownloadAll} alt="Video" style={{width: "80%", marginLeft: "18px"}}/>
            </div>
          </td>
          <td colSpan = "5">
            <div>
            Descargar grabación y fotos del examen.
            </div>
          </td>
        </tr>
        }
        <tr style={{height: "55%"}}></tr>
      </tbody>
    </table>
	);
}
function hideSidePanel(el){
  el.props.setHideSidePanel(true);
  el.props.setCountdownFrom(parseInt(document.getElementById("countdownFromInput").value));
  let div = document.getElementById("canvasDiv");
  let btns = document.getElementById("buttons");
  let canvas = document.getElementById("canvasPreview");
  el.canvasHeight = div.clientHeight*100/window.innerWidth; //vw
  el.canvasWidth = div.clientWidth*100/window.innerWidth; //vw
  document.getElementById("WebcamStream_Wrapper").style.width = "100vw";
  if ((87*el.canvasWidth/el.canvasHeight)*window.innerHeight/100 < window.innerWidth){
    div.style.height = "87vh";
    div.style.width = 87*el.canvasWidth/el.canvasHeight+"vh";
  }
  else{
    div.style.width = "90vw";
    div.style.height = 90*el.canvasHeight/el.canvasWidth + "vw";
  }

  btns.style.width = div.clientWidth*100/window.innerWidth+"vw";
  btns.style.top = (div.clientHeight - btns.clientHeight)*100/window.innerHeight + "vh";
  div.style.marginLeft = (100-(div.clientWidth*100/window.innerWidth))/2 + "vw";
  canvas.style.height="100%";
  canvas.style.width="100%";
  btns.style.top = (div.clientHeight - btns.clientHeight)*100/window.innerHeight + "vh";
  btns.style.opacity = 0;

  canvas.addEventListener("mouseover",showButtons, false);
  btns.addEventListener("mouseover",showButtons, false);
  canvas.addEventListener("mouseout",hideButtons,false);

  document.getElementById("container").style.paddingTop = "2vh";

}
function showSidePanel(el){
  el.props.setHideSidePanel(false);
  document.getElementById("WebcamStream_Wrapper").style.width = "55vw";
  let btns = document.getElementById("buttons");
  let div = document.getElementById("canvasDiv");
  let canvas = document.getElementById("canvasPreview");
  div.style.width = el.canvasWidth+ "vw";
  div.style.height = el.canvasHeight + "vw";
  canvas.style.width="100%";
  canvas.style.height="100%";
  div.style.marginLeft = "2.5vw";
  div.style.marginRight = "2.5vw";
  btns.style.width = "50vw";
  btns.style.top = el.canvasHeight + "vw";
  btns.style.opacity = 1;
  canvas.removeEventListener("mouseover",showButtons,false);
	btns.removeEventListener("mouseover",showButtons,false);
	canvas.removeEventListener("mouseout",hideButtons,false);
  setTimeout(function(){
  if(document.getElementById("countdownFromInput")){
    document.getElementById("countdownFromInput").value = el.props.countdownFrom;
    listDevices();
    }},100);

  document.getElementById("container").style.paddingTop = "4vh";
}
function showButtons(){
	var btns= document.getElementById("buttons");
  if(btns){	btns.style.opacity=0.7;}
}

function hideButtons(){
	var btns= document.getElementById("buttons");
  if(btns){btns.style.opacity=0;}
}


export default SidePanel;
