import './InferenceUI.css';
import {Container
    } from 'reactstrap';
   /* Container, Col, Row,
    Form, FormGroup, Label, Input,
    Card, CardImg, CardBody, CardTitle, CardText } from 'reactstrap';*/
import React, {useRef, useEffect} from 'react';
//import io from 'socket.io-client';

class WebcamStream extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isVideoLoading: true
    };

    this.videoTag = React.createRef();
    this.canvas = React.createRef();

    this.tick = this.tick.bind(this);
  }

  componentDidMount() {
    navigator.mediaDevices
      .getUserMedia({ video: { facingMode: "environment" } })
      .then(stream => {
        this.videoTag.current.srcObject = stream;
        requestAnimationFrame(this.tick);
        window.stream = stream;
      });
  }

  tick() {
    const video = this.videoTag.current;

    const checkVideoState = setInterval(() => {
      if (video.readyState === video.HAVE_ENOUGH_DATA) {
        clearInterval(checkVideoState);

        this.setState({ isVideoLoading: false });

          const canvasElement = this.canvas.current;
          const canvas = canvasElement.getContext("2d");

          canvasElement.height = video.videoHeight;
          canvasElement.width = video.videoWidth;
          canvas.drawImage(
            video,
            0,
            0,
            canvasElement.width,
            canvasElement.height
          );
        requestAnimationFrame(this.tick);
      }
    }, 50);
  }

  render() {
    const { isVideoLoading } = this.state;

    return (
      <div>
        <video
          ref={this.videoTag}
          width="400"
          height="400"
          autoPlay
          style={{ display: "none" }}
        />

        {!isVideoLoading && <canvas ref={this.canvas} />}

        {isVideoLoading && <p>Please wait while we load the video stream.</p>}
      </div>
    );
  }
}
