import {Modal, ModalHeader, ModalBody, ModalFooter 
    } from 'reactstrap';
import React, {useState}from 'react';
//import './InferenceUI.css'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import JSZip from 'jszip';
import JSZipUtils from 'jszip-utils';
import FileSaver from 'file-saver';

class Screenshots extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      lastScreenshotID: 0,
      modalOn: false,
      //slidesData: []
    };
    this.selected = 0;
  }

	captureScreenshot = async () => {
  	var canvas = document.querySelector("#canvasPreview");
		var imgAsDataURL = canvas.toDataURL("image/jpeg",1);

    let newSlidesData = [{src: imgAsDataURL, 
                          thumbnail: imgAsDataURL,
                          thumbnailWidth: 320,
                          thumbnailHeight: 240
                        }];
    this.props.setScreenshots(newSlidesData);
		await this.setState({lastScreenshotID: this.state.lastScreenshotID+1});
	/*	
	  let thumbs = document.getElementsByClassName("thumb");
		thumbs[thumbs.length-1].onclick = async(a) => {
			if(this.props.currentState.enabledModalScreenshots){
        this.selected = a.target.childNodes[0].id;
        this.setState({modalOn: true});

			}
		};	

    let btnsCarousel = document.getElementsByClassName("control-arrow");
		try{
			btnsCarousel[2].style.backgroundColor= 'black';
			btnsCarousel[3].style.backgroundColor= 'black';
		}
		catch{
		}
		document.querySelector(".carousel-slider").style.display = 'none';
    */

}
  getScreenshots(){
    return this.state.slidesData;
  }
	download(){
		let zip = new JSZip();
		for(let i=0;i < this.props.currentState.screenshots.length; i++){
			let name = "Screenshot_" + i + ".jpeg";
			zip.file(name,urlToPromise(this.props.currentState.screenshots[i].src),{binary:true});
		}
		zip.generateAsync({type: "blob"}).then(function(content) {
  		FileSaver.saveAs(content, "download.zip");
		});
	}

	render(){
    //let classNameToRender = (this.props.currentState.fullScreen)? "FullScreen":"RegularScreen";
    return(
			<div id="screenshots">
			</div>
    );
	}

}

const ModalScreenshots = (props) => {
  const {
    className,
    data,
    selected,
  } = props;
  const [modal, setModal] = useState(true);

  const toggle = () => {
    setModal(!modal); 
		props.handler();
  }
  let btnsCarousel = document.getElementsByClassName("control-arrow");

  try{
    btnsCarousel[6].style.backgroundColor = 'green';
    btnsCarousel[6].style.zIndex= 5;

	}
	catch(e){}

  return (
    <div>
      <Modal isOpen={modal} toggle={toggle} className={className} size={'xl'}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
        <Carousel showArrows={true} selectedItem={selected} id="modalScreenshot" >
          {data.map((slide) =>
            <div key={0}>
              <img className="slick-slide-image" src={slide.src} alt='Error' />
            </div>
          )}
        </Carousel>
        </ModalBody>
        <ModalFooter>
        </ModalFooter>
      </Modal>
    </div>
  );
}
//convert imgs to download
function urlToPromise(url) {
  return new Promise(function(resolve, reject) {
    JSZipUtils.getBinaryContent(url, function (err, data) {
      if(err) {
        reject(err);
      }
      else {
        resolve(data);
      }
    });
  });
}
export default Screenshots;
