import { Button, Progress, Container
} from 'reactstrap';
import React from 'react';
//import './InferenceUI.css'

class ElementsMain extends React.Component{
  constructor(props){
      super(props);
      this.state = {
        progress: "30"
      }
  }

  changeProgress(val){
    //this.setState({progress: val.toString(10)});
    console.log(this.props.currentState.progress);
    console.log(typeof this.state.progress);
  }
  render(){
    console.log("renderin");
    return(
      <Container> 
        {this.props.currentState.showStartBtn && <Button color="primary" onClick={() => startStopFunction(this)}>{this.props.currentState.textMainBtn}</Button>}
        {this.props.currentState.showCaptureBtn && <Button color="primary" disabled={this.props.currentState.disableBtnCapture}  onClick={() => this.props.captureScreenshot()}>{"Capture Screenshot"}</Button>}
        {this.props.currentState.showProgressBar && <Progress value={this.state.progress}/>}

			  <Button color= "secondary" onClick={()=>this.props.fromServer("blur")}>{"Blur"}</Button>
        <input type="range" min="0" max="100" id="range" onChange={(e)=>
                {this.props.fromServer("progress",e.target);
                console.log(e.target.value);}}/>
      </Container>
    );
  }
}
function startStopFunction(btn){
  if (btn.props.currentState.textMainBtn.localeCompare("Start") === 0){
    startFunction(btn);
    return;
  }
  stopFunction(btn);
}

function startFunction(btn){
  btn.props.handler("Countdown");
  btn.props.currentState.almostStreaming = true;

  }

function stopFunction(btn){
   btn.props.handler("StopStream");

}


export default ElementsMain;
