import './WebcamStream.css'
import React from 'react';
import {Button, Alert, Progress} from 'reactstrap';



import {handleDataAvailable, setProgressBarColor, ctrlBtnFunction, countdown, urlToPromise, downloadScreenshots} from './functions.js';

// Agregar variable para almacenar la hora en que se apretó el start.
// Administrar el fullscreen desde acá ahora.
class WebcamStream extends React.Component {
  constructor(props) {
    super();
    this.state = {
      isVideoLoading: true
    };

    this.videoTag = React.createRef();
    this.canvasPreview = React.createRef();
		this.selectVideoSrc = React.createRef();
    this.tick = this.tick.bind(this);

    this.countdownFrom = 1;
    this.timeStartPressed = null;

    this.mediaRecorder = null;
    this.recordedChunks= [];
	  this.videoRecorded = null;

  }

  setStream(){
    var selectVideoSrc = document.getElementById("videoSelectConf");
    // Falta cerrar el stream anterior antes de abrir uno nuevo.
    navigator.mediaDevices.getUserMedia({
      video: {
        deviceId: selectVideoSrc.value? {exact: selectVideoSrc.value}:undefined,
        facingMode: "enviroment"
      }
    }).then(stream => {
      this.videoTag.current.srcObject = stream;
      requestAnimationFrame(this.tick);
      window.stream = stream;
      var options = { mimeType: "video/webm;codecs=vp8" };
      this.mediaRecorder = new MediaRecorder(stream, options);
    });
  }

  tick() {
    const video = this.videoTag.current;
    const checkVideoState = setInterval(() => {
      if (video.readyState === video.HAVE_ENOUGH_DATA) {
        clearInterval(checkVideoState);

        this.setState({ isVideoLoading: false });
          const canvasPreviewElement = this.canvasPreview.current;
          if(canvasPreviewElement){
          const canvasPreview = canvasPreviewElement.getContext("2d");

          canvasPreviewElement.height = video.videoHeight;
          canvasPreviewElement.width = video.videoWidth;
          canvasPreview.drawImage(
              video,
              0,
              0,
              canvasPreviewElement.width,
              canvasPreviewElement.height
           );
           if(this.props.currentState.stateName.localeCompare("Countdown")===0){
             canvasPreview.font = "200px Arial"
             countdown(canvasPreview,this);
           }
           requestAnimationFrame(this.tick);
           }
      }
    }, 50);
  }

  startRecording(){
      this.mediaRecorder.ondataavailable = handleDataAvailable(this);
      this.mediaRecorder.start();
      this.props.getVideoThumbnail(this.canvasPreview.current.toDataURL("image/jpeg",1));
  }

	stopRecording(){
    this.mediaRecorder.stop();
	}

	downloadVideo(){
		var aux = document.createElement("a");
		document.body.appendChild(aux);
		aux.style = "display: none";
		aux.href = this.videoRecorded;
    aux.download = "test.webm";
    document.body.appendChild(aux);
		aux.click();		
	}

  captureScreenshot(){
    var canvas = document.querySelector("#canvasPreview");
    var imgAsDataURL = canvas.toDataURL("image/jpeg",1);
    let newSlidesData = [{src: imgAsDataURL, 
                          thumbnail: imgAsDataURL,
                          thumbnailWidth: 160,
                          thumbnailHeight: 120
                        }];
    this.props.setScreenshots(newSlidesData);
    }

  downloadScreenshots(){
    downloadScreenshots(this);
  }





  componentDidMount(){
		//initial message on canvas
    let cnvs = document.getElementById("canvasMessage");
    cnvs.style.height = cnvs.clientWidth*3/4+"px";
		cnvs.style.backgroundColor= "black";
		var ctx = cnvs.getContext("2d");
		ctx.font = "10px Arial";
		ctx.fillStyle = "white";
		ctx.fillText("Seleccione un dispositivo de imagen en" , cnvs.width/10, 3*cnvs.height/8);
		ctx.fillText("el panel de  configuración.", cnvs.width/10, 4*cnvs.height/8);
  }

  render() {
    const isVideoLoading = this.state.isVideoLoading;
    return (
      <div className="WebcamStream_Wrapper">
        {/* Dummy video tag to recieve UserMedia stream.*/}
        <video
          ref={this.videoTag}
          autoPlay
          id = "video"
          style={{ display: "none"}}
        />

        {!isVideoLoading &&
            <div id="canvasDiv">
              {this.props.currentState.canvasAlert &&
                <div id="alert" style={{position: 'absolute', zIndex: 2}}>
                  <Alert color="warning">Verifique el enfoque del dispositivo.</Alert>
                </div>
              }

              {this.props.currentState.stateName.localeCompare("GetCon") === 0 &&
                <div id="alert" style={{position: 'absolute', zIndex: 2}}>
                  <Alert color="info">Estableciendo conexión con el servidor.</Alert>
                </div>
              }

              {this.props.currentState.renderCanvas &&
                <div>
                  <canvas ref={this.canvasPreview}
                          id="canvasPreview"
                          className="canvasRendered"/>

                </div>
              }
              {!this.props.currentState.renderCanvas &&
                <video
                  id = "recordedVideoElement"
                  controls
                  srcObject = {null}
                  src={null}
                  className="canvasRendered"/>
              }
            </div>
        }
        {isVideoLoading &&
          <div>
            <canvas id="canvasMessage"
                    className="canvasRendered"/>
          <p>Seleccione un dispositivo de imagen en el panel de configuración.</p>

          </div>
        }
				<div id="buttons">
          <Button color="primary"
                  size="xl"
                  onClick={()=>{ctrlBtnFunction(this);}}
                  disabled={!this.props.currentState.enableCtrlBtn}>
                  {this.props.currentState.textCtrlBtn}</Button>
          <Button color="primary" 
                  size = "l"
                  disabled={!this.props.currentState.enableCaptureBtn}  
                  onClick={() => this.captureScreenshot()}>
            {"Capturar Pantalla"}</Button>
        {this.props.currentState.showProgressBar && 
          <Progress value={this.props.currentState.progress} 
                    color={setProgressBarColor(this)}/>}
        </div>
      </div>
    );
  }
}

function downloadVideo(el){
  var aux = document.createElement("a");
  document.body.appendChild(aux);
  aux.style = "display: none";
  aux.href = el.videoRecorded;
  aux.download = "test.webm";
  document.body.appendChild(aux);
  aux.click();		
}


export default WebcamStream;
