import './SidePanel.css'
import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Card,CardImg, Button,  CardText, Row, Col, Container} from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
import {listDevices} from './functions.js'

class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
      hidePanel: false
		}
    this.canvasHeight = 0;
    this.canvasWidth = 0;
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
	render(){
		return(
			<div>
        {!this.state.hidePanel && 
			    <div id="sidePanel_Wrapper">
				    <SideTabs
                 		thumbnail={this.props.thumbnail} 
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		screenshots={this.props.currentState.GallerySrc}
                 		currentState={this.props.currentState}
										superElement={this}
            />
          {!this.props.currentState.isVideoLoading &&
            <div id="hideTabsBtn" className="ctrlTabs" onClick={()=>hideSidePanel(this)}></div>}
					</div>}

        {this.state.hidePanel && 
          <div id="showSidePanel" className="ctrlTabs" onClick={()=>showSidePanel(this)}></div> 
        }
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    screenshots,
    currentState,
		superElement,
  } = props;
  const [activeTab, setActiveTab] = useState('4');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }


  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '1' })}
            onClick={() => { toggle('1'); }} >
            Diagnostico
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('2'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('3'); }}>
            Capturas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '4' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('4'); }}>
           Configuración 
          </NavLink>
        </NavItem>
      </Nav>
      <div id="TabsBody">
      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <DiagnosisTab />
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler} screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="3">
          <GalleryTab screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="4">
					<ConfigTab superElement={superElement} />
        </TabPane>
      </TabContent>
      </div>
    </div>
  );
}
function GalleryTab(props){
  return(
    <Gallery images={props.screenshots} rowHeight="80"/>
  );
}
function ConfigTab(props){
  return(
		<div id="configTab">
      <Row>
        <p>Seleccione dispositivo:</p>
        <Col>
		      <select id="videoSelectConf"/>
        </Col>
        <Col>
			    <Button color='secondary'
                  outline
                  onClick={()=>{props.superElement.props.setStreamFromConfig()}}
                  id="selectSrcBtn"
          >Ok</Button>
        </Col>
      </Row>
      <Button onClick={()=>{props.superElement.props.mirror("horizontal")}}> MH </Button>
      <Button onClick={()=>{props.superElement.props.mirror("vertical")}}> MV </Button>
    	<Button id="RefreshBtn"
              color ='primary'
              onClick={()=>{listDevices();}}
    		      >Refrescar</Button>
		</div>
  );
}


function DiagnosisTab(){
	return(
		<Spinner color="info" style={{position:'absolute',margin:'auto'}} size={'xl'}/>
	);
}

const DownloadTab = (props) => {
  const{
    thumbnail,
    downloadHandler,
    screenshots
  }=props;

  let  screenshotsIsEmpty = (screenshots.length === 0)? true:false;
	return(
		<Container>
			<Row>
      	<Col sm="5">
          <Card body>
            <CardImg top  src= {thumbnail} alt="thumbnail" />
            <CardText>Grabación del examen.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("video")}}> Descargar</Button>
          </Card>
        </Col>
        {!screenshotsIsEmpty &&
        <Col sm="5">
          <Card body>
            <CardImg top  src= {screenshots[0].src} alt="thumbnail" />
            <CardText>Capturas de Pantalla.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("screenshots")}}>Descargar</Button>
          </Card>
        </Col>
        }
     </Row>
		</Container>
	);
}
function hideSidePanel(el){
  el.setState({hidePanel: true});
  document.getElementById("WebcamStream_Wrapper").style.width = "100vw";
  let canvas = document.getElementById("canvasDiv");
  //let canvas = document.getElementById("WebcamStream_Wrapper");
  let btns = document.getElementById("buttons");
  console.log(btns);
  el.canvasHeight = canvas.clientHeight;
  el.canvasWidth =  canvas.clientWidth;
  canvas.style.height = "77vh";
  canvas.style.width = 77*el.canvasWidth/el.canvasHeight+"vh";
  canvas.style.marginLeft = "20%";
  canvas.style.marginRight = "20%";
  btns.style.width = 77*el.canvasWidth/el.canvasHeight+"vh";
  btns.style.marginLeft = "20%";

}
function showSidePanel(el){
  el.setState({hidePanel: false});
  document.getElementById("WebcamStream_Wrapper").style.width = "55vw";
  let canvas = document.getElementById("canvasDiv");
  //let canvas = document.getElementById("WebcamStream_Wrapper");
  canvas.style.width = "90%";
  canvas.style.position = "absolute";
  canvas.style.marginLeft = "5%";
  canvas.style.marginRight = "5%";
}
export default SidePanel;
