	constructor(props){
		super(props);
		this.state= {
			text: "Start",
			timeStartPressed: 0,
			almostStreaming: false,
			streaming: false
		}
	}

	render(){
		return(
			<button onClick="countdown(10)">{this.state}</button>
		);
	}
}
