import './SidePanelWrapper.css'
import './ConfTab.css'
import './ResultsTab.css'
import './DiagnosisTab.css'
import { Spinner,  Button } from 'reactstrap';
import React, {useState} from 'react';
import {listDevices, setGallery2, galleryPrev, galleryNext, removeAllImgBorders} from './functions.js'
import {getSysDetails} from './getNavDetais.js'
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

import logoDownload from './download.png' 
import logoFlipH from './flipH_2.png'
import logoFlipV from './flipV_2.png'
import logoResetFlip from './reset_image.png'
import logo from './logo.png'
import logoTrash from './trash.png'
import logoTick from './tick.png'
import frame138 from './MaterialDiagnostico/frame138.png'
import frame302 from './MaterialDiagnostico/frame302.png'
import frame1282 from './MaterialDiagnostico/frame1282.png'
import pdf from './sample.pdf'
//import iconHidePanel from './hidePanel.png'
import iconShowPanel from './showPanel.png'
import iconError from './error.png'
class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
      showDiagnosisImg: false
		}
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
  setGallery2(){
    setGallery2(this.props);
  }
  componentDidMount(){
    listDevices();
    document.getElementById("countdownFromInput").value="1";
    changeTab(1, this.props.setTabOn, this.props.currentState);
  }
  saveCanvasDim(w,h){
    this.props.saveCanvasDim(w,h);
  }
  getCanvasDim(){
    return this.props.getCanvasDim();
  }

  galleryCtrl(dir, galleryIndxSh, gallerySrc, newPhoto, selectedPhoto){
    if(dir.localeCompare('prev') === 0){
      galleryPrev(galleryIndxSh, gallerySrc, newPhoto, selectedPhoto);
    }
    else if(dir.localeCompare('next') === 0){
      galleryNext(galleryIndxSh, gallerySrc, newPhoto, selectedPhoto);
    }
  }
  hideSidePanel(){
    hideSidePanel(this);
  }
  setShowDiagnosisImg(value,indx){
    //areglar
    this.superElement.setState({showDiagnosisImg: value},()=>{
      document.getElementById('modalImg').src = document.getElementById('diagnosisImg'+indx).src;
      document.getElementById('modalImgTag').innerHTML('holi');
    });
  }
  render(){
		return(
			<div>
        {!this.props.currentState.hideSidePanel && 
			    <div id="sidePanel_Wrapper">
            <Tabs tabOn={this.props.currentState.tabOn}
                 	currentState={this.props.currentState}
                  setTabOn={this.props.setTabOn}

            />
				    <TabsBody
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		currentState={this.props.currentState}
                    gallerySrc={this.props.gallerySrc}
                    setGalleryIndxShowing={this.props.setGalleryIndxShowing}
                    setSelectedGalleryPhoto={this.props.setSelectedGalleryPhoto}
                    selectedGalleryPhoto = {this.props.currentState.selectedGalleryPhoto}
										superElement={this}
                    showDiagnosisImg = {this.state.showDiagnosisImg}
                    setShowDiagnosisImg = {this.setShowDiagnosisImg}
            />
						<ModalError className=''/>

            {/*!this.props.currentState.isVideoLoading  &&  this.props.currentState.renderCanvas &&
              <div id="hideSidePanel"  onClick={()=>this.hideSidePanel()}>
                <img id='iconHidePanel' src={iconHidePanel} alt=''/>
              </div>}
              */}
					</div>}

        {this.props.currentState.hideSidePanel && 
          <div id="showSidePanel"  onClick={()=>{showSidePanel(this)}}>
            <img id='iconShowPanel' src={iconShowPanel} alt=''/>
          </div>
        }
			</div>
		);
	}
}

function TabsBody(props) {
  if(props.currentState.tabOn === 1){
    return(
			<ConfigTab superElement={props.superElement} className='tabBody'/>
    );
  }
  else if(props.currentState.tabOn === 2){
    return(
      <GalleryTab gallerySrc={props.gallerySrc} 
                  galleryIndxSh={props.currentState.GalleryIndexShowing}
                  setGalleryIndxShowing={props.setGalleryIndxShowing}
                  setSelectedGalleryPhoto={props.setSelectedGalleryPhoto}
                  selectedPhoto = {props.currentState.selectedGalleryPhoto}
                  downloadHandler = {props.downloadHandler}
                  selectedGalleryPhoto = {props.selectedGalleryPhoto}
                  currentStateName= {props.currentState.stateName}
                  />
    );
  }
  else if(props.currentState.tabOn === 3){
    return(
      <DiagnosisTab superElement={props.superElement} 
                    showDiagnosisImg = {props.showDiagnosisImg}
                    setShowDiagnosisImg = {props.setShowDiagnosisImg}
      />
    );
  }
  else if(props.currentState.tabOn === 4){
    return(
      <ReportTab className='tabBody'/>
    );
  }
  else{
    return(
      <div>
      <p>Error</p>
      </div>
    );
  }
}
function Tabs(props){
  return(
    <div id='tabs'>
      <div id='confTab' className='tab' 
           onClick={()=>changeTab(1, props.setTabOn, props.currentState)}>
        <i className="fas fa-cog"></i>
      </div>
      <div id='galleryTab' className='tab'
           onClick={()=>changeTab(2, props.setTabOn, props.currentState)}>
        Resultados de  Examinación  
      </div>
      <div id='resultsTab' className='tab'
           onClick={()=>changeTab(3, props.setTabOn, props.currentState)}>
        Diagnóstico
      </div>
      <div id='reportTab' className='tab'
           onClick={()=>changeTab(4, props.setTabOn, props.currentState)}>
        Informe Médico
      </div>
    </div>
  );
}
function changeTab(n, setTabOn,state){
  if(state.tabsEnabled[n-1]){ 
    setTabOn(n);

  }
}
function ConfigTab(props){
  return(
		<div id="configTab" className='tabBody'>
      <table className = "tableDefault fullSpace">
        <tbody className ="fullSpace">

          <tr>
            <td colSpan="5" style={{}}>
              <div>Seleccione dispositivo:</div>
            </td>
            <td colSpan="5"> 
  		          <select id="videoSelectConf"/>
            </td>
            <td colSpan="2">
  			        <div onClick={()=>{props.superElement.props.setStreamFromConfig()}}
                     id="selectSrcBtn"
                     className='button'
                >Ok</div>
            </td>
            <td colSpan="1">
      	    <span id="RefreshBtn"
                    onClick={()=>{listDevices();}}
                    >
  				    <i className="fas fa-sync-alt"></i>
  			    </span>

            </td>
          </tr>


          <tr style={{height: "5%"}}/>

          <tr style={{height:"2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="13">
              <div>Orientación de la imagen:</div>
            </td>
          </tr>

          <tr style={{height: "2%"}}/>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorHBtn" onClick={()=>{props.superElement.props.mirror("horizontal")}}>
                <img src={logoFlipH} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar horizontalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorVBtn" onClick={()=>{props.superElement.props.mirror("vertical")}}>
                <img src={logoFlipV} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar verticalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorRstBtn" onClick={()=>{props.superElement.props.mirror("reset")}}>
                <img src={logoResetFlip} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Restablecer</div>
            </td>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr style={{height: "2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="5">
              <div>Cuenta regresiva desde: </div>
            </td>
            <td colSpan="5">
              <input  type="number" id="countdownFromInput" min="1" max="20"/> 
            </td>
          </tr>

          <tr style={{height: "20%"}}> 
          </tr>
        </tbody>
      </table>
		</div>

  );

}

function DiagnosisTab(props){
	return(
      <div className = "tabBody">
        {!props.superElement.props.currentState.isDiagnosisReady &&

          <table className = "tableDefault fullSpace">
            <tbody className = 'fullSpace'>
              <tr style={{height: "10%"}}/>
              <tr style={{height: "30%"}}>
                <td colSpan="1"/>
                <td colSpan="2">
                  <img src={logo} alt="logo" style={{width: "100%"}}/>
                </td>
                <td colSpan="1"/>
              </tr>
              <tr style={{height: "10%"}}>
                <td colSpan="1"/>
                <td colSpan="2" style={{textAlign: "center"}}>
                  <div> Procesando... </div>
                </td>
                <td colSpan="1"/>
              </tr>
              <tr style={{height: "10%"}}>
                <td colSpan="1"/>
                <td colSpan="2" style={{textAlign: "center"}}>
  	              <Spinner color="info" style={{margin:'auto'}} size={'xl'}/>
                </td>
                <td colSpan="1"/>
              </tr>
              <tr style={{height: "40%"}}/>
            </tbody>  
          </table>
        }

        {props.superElement.props.currentState.isDiagnosisReady &&
          <div>
            <div id= "diagnosisLeft">

              <img id='diagnosisImg1' className='diagnosisImg' src= {frame138} alt="frame" 
                   onClick={()=>{props.setShowDiagnosisImg(true,1)}}
              /> 
              <img id='diagnosisImg3' className='diagnosisImg' src= {frame1282} alt="frame" 
                   onClick={()=>openDiagnosisImg(2)}
              />
              <img id='diagnosisImg2' className='diagnosisImg' src= {frame302} alt="frame" 
                   onClick={()=>openDiagnosisImg(3)}
              />
              <img id='diagnosisImg4' className='diagnosisImg' src= {frame1282} alt="frame" 
                   onClick={()=>openDiagnosisImg(4)}
              />

              <div id='diagnosisImgTag1' className='diagnosisImgTag'>
                <span className='d_ImgTagSpan'> Tímpano normal 
                <div id='d_ImgTagUnderline1' className='d_ImgTagUnderline'/>
                </span>
              </div>
              <div id='diagnosisImgTag2' className='diagnosisImgTag'>  
                <span className='d_ImgTagSpan'> Exostosis del conducto
                <div id='d_ImgTagUnderline2' className='d_ImgTagUnderline'/>
                </span>
              </div>
              <div id='diagnosisImgTag3' className='diagnosisImgTag'>  
                <span className='d_ImgTagSpan'> Cera
                <div id='d_ImgTagUnderline3' className='d_ImgTagUnderline'/>
                </span>
              </div>
              <div id='diagnosisImgTag4' className='diagnosisImgTag'> 
                <span className='d_ImgTagSpan'>Exostosis del conducto 
                <div id='d_ImgTagUnderline4' className='d_ImgTagUnderline'/>
                </span>
              </div>


            </div>

            <div id='diagnosisBoxTag1' className='diagnosisBoxTag'> Diagnóstico AMIRA:</div>
            <div id='diagnosisBoxTag2' className='diagnosisBoxTag'> Diagnóstico final:</div>
            <div id='diagnosisBoxTag3' className='diagnosisBoxTag'> Observaciones:</div>

            <div id='diagnosisBox1' className = "diagnosisBox"  >
                <ul className = "listNoneItem">
                  <li> Exostosis del conducto </li>
                  <li> Tímpano normal </li>
                  <li> Cera  </li>
               </ul>
            </div>

            <textarea id='diagnosisBox2' className= 'diagnosisBox' cols="20" rows="1"></textarea>
            <textarea id='diagnosisBox3' className= 'diagnosisBox' cols="20" rows="1"></textarea>
            <div id='btnMakeReport'>
              Generar Informe
            </div>
        {props.showDiagnosisImg &&
          <div id='darkBackground'>
            <div id='modal'>
              <img id='modalImg' alt=''/>
              <legend id='modalImgTag'/>
            </div>
          </div>
        }
            </div>
        }
      </div>
	);
}
function openDiagnosisImg(indx){
  console.log('aaaaaa');
  return(
    <div id='tst'/>
  );
}
function GalleryTab(props){
  let galleryIndxSh = props.galleryIndxSh;
  let mapKeys = Array.from(props.gallerySrc.keys());
  let isEmpty = (galleryIndxSh[0] === 0)? true:false;
  return(
    <div className = 'tabBody'>
      <div id = 'galleryPrev' className='button' href='galleryPrev'
            onClick = {()=>{props.setGalleryIndxShowing(
                           [galleryIndxSh[0], galleryIndxSh[1], 
                           mapKeys[mapKeys.indexOf(galleryIndxSh[0])-1]],
                    [1,2,0], false,'prev');}} />
      <div id = 'galleryNext' className='button'
           onClick = {()=>{props.setGalleryIndxShowing(
                     [galleryIndxSh[1], galleryIndxSh[2], 
                      mapKeys[mapKeys.indexOf(galleryIndxSh[2])+1]],
                    [0,1,2], false,'next')}} />
      <div id='downloadBG'>
        <div id = 'downloadAll' className= 'download button'
              onClick={()=>{
                console.log(props.currentStateName);
                if(props.currentStateName.localeCompare('Review') === 0)
                  props.downloadHandler("all")}}>
              <img src={logoDownload} alt='' className='downloadLogo'/>
              <div className='downloadTag'>
              Descargar fotos y video
              </div>
        </div>
        <div id = 'downloadPhotos' className= 'download button'
              onClick={()=>{
                if(props.currentStateName.localeCompare('Review') === 0)
                  props.downloadHandler("screenshots")}}>
              <img src={logoDownload} alt='' className='downloadLogo'/>
              <div className='downloadTag'>
              Descargar fotos 
              </div>
        </div>
        <div id = 'downloadVideo' className= 'download button'
              onClick={()=>{
                if(props.currentStateName.localeCompare('Review') === 0)
                  props.downloadHandler("video")}}>
              <img src={logoDownload} alt='' className='downloadLogo'/>
              <div className='downloadTag'>
              Descargar video
              </div>
        </div>
      </div>
      {!isEmpty &&
      <div className>
        <div id='deletePhoto' className='button'>
          <img src={logoTrash} alt='' id='logoCtrlPhoto'
               onClick={()=>deletePhoto(props.gallerySrc, props.selectedGalleryPhoto, props.galleryIndxSh,props.setGalleryIndxShowing, props.setSelectedGalleryPhoto)}
          />
        </div>
        {/*
        <div id='keepPhoto' className='button'>
          <img src={logoTick} alt='' id='logoCtrlPhoto'/>
        </div>*/
        }
      </div>
      }

      {isEmpty &&
        <div id='divNoPhoto'>
          <div id='divNoPhotoInside'>
            No se encuentran fotos disponibles.
          </div>
        </div>
      }
      <img  src='' id= 'img1' alt='' 
            onClick={()=>{clickOnImage(1);
                          props.setSelectedGalleryPhoto(galleryIndxSh[0]);
            }}/> 
      <img  src='' id='img2' alt='' 
            onClick={()=>{clickOnImage(2) 
                          props.setSelectedGalleryPhoto(galleryIndxSh[1]);
            }}/>
      <img  src='' id='img3' alt='' 
            onClick={()=>{clickOnImage(3) 
                          props.setSelectedGalleryPhoto(galleryIndxSh[2]);
            }}/>
      <img id='displayedImg' src={props.gallerySrc.get(props.selectedPhoto)} alt=''/>
      {/*
			<div id='errorBtn' className='button' onClick={()=>document.getElementById('modalBtn').click()}>
				<img id='iconError' src={iconError} alt=''/>
			</div>*/
      }
    </div>
  );
}
async function deletePhoto(gallerySrc, selected, galleryIndxSh, setGalleryIndxSh, setSelectedPhoto){
  let newKeysArr = [0,0,0];
  for (let i = 0; i < galleryIndxSh.length; i++){
    if (galleryIndxSh[i] === selected){
      let mapKeys = Array.from(gallerySrc.keys());
      let indx = mapKeys.indexOf(selected);
      for(let j =0; j < galleryIndxSh.length-i; j++){
        if(mapKeys.length <= indx+1+j){
          newKeysArr[j+i] = 0;
        }
        else{
          newKeysArr[j+i] = mapKeys[indx+1+j];
        }
      }
      if(i === 0){
        if(newKeysArr[0] === 0){
          if(mapKeys[indx-1]){
            document.getElementById('galleryPrev').click();
            newKeysArr[0] = mapKeys[indx-1];
          }
          else{
          }
        }
        setSelectedPhoto(newKeysArr[0]);
        await sleep(200);
        clickOnImage(1);
      }
      else{
        setSelectedPhoto(newKeysArr[i-1]);
        clickOnImage(i);
      }
      break;
    }
    else{
     newKeysArr[i] = galleryIndxSh[i]; 
    }
  }
  if(newKeysArr[0] === 0)
    removeAllImgBorders();
  gallerySrc.delete(selected);
  setGalleryIndxSh(newKeysArr,[0,1,2],false,'delete');
}
function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}
function ReportTab(props){
  return(
    <div className='tabBody'>
      <embed id='pdf' src={pdf}/>
    </div>
  );
}

function clickOnImage(number){
  for (let i=1; i <= 3; i++){
    let name = 'img'.concat(i);
    if (i !== number){
      document.getElementById(name).style.borderStyle = 'none';
      document.getElementById(name).style.top = '1.8vw';
      document.getElementById(name).style.width = '10vw';
      document.getElementById(name).style.left = 5 + (i-1)*11 + 'vw';
    }
    else{
      document.getElementById(name).style.border= '0.5vw solid #021a40';
      document.getElementById(name).style.top = '1.3vw';
      document.getElementById(name).style.width = '11vw';
      document.getElementById(name).style.left = 5 + (i-1)*11 - 0.5 + 'vw';

    }
  }
}

function hideSidePanel(el){
  el.props.setHideSidePanel(true);
  if(!el.props.currentState.stateName.localeCompare('Idle'))
    el.props.setCountdownFrom(parseInt(document.getElementById("countdownFromInput").value));
  let div = document.getElementById("canvasDiv");
  let btns = document.getElementById("buttons");
  let canvas = document.getElementById("canvasPreview");
  el.canvasHeight = div.clientHeight*100/window.innerWidth; //vw
  el.canvasWidth = div.clientWidth*100/window.innerWidth; //vw
  document.getElementById("WebcamStream_Wrapper").style.width = "100vw";
  if ((87*el.canvasWidth/el.canvasHeight)*window.innerHeight/100 < window.innerWidth){
    div.style.height = "87vh";
    div.style.width = 87*el.canvasWidth/el.canvasHeight+"vh";
  }
  else{
    div.style.width = "90vw";
    div.style.height = 90*el.canvasHeight/el.canvasWidth + "vw";
  }

  btns.style.width = div.clientWidth*100/window.innerWidth+"vw";
  btns.style.top = (div.clientHeight - btns.clientHeight)*100/window.innerHeight + "vh";
  div.style.marginLeft = (100-(div.clientWidth*100/window.innerWidth))/2 + "vw";
  canvas.style.height="100%";
  canvas.style.width="100%";
  btns.style.top = (div.clientHeight - btns.clientHeight)*100/window.innerHeight + "vh";
  btns.style.opacity = 0;

  canvas.addEventListener("mouseover",showButtons, false);
  btns.addEventListener("mouseover",showButtons, false);
  canvas.addEventListener("mouseout",hideButtons,false);

  document.getElementById("container").style.paddingTop = "2vh";

}

function showSidePanel(el){
  el.props.setHideSidePanel(false);
  document.getElementById("WebcamStream_Wrapper").style.width = "55vw";
  let btns = document.getElementById("buttons");
  let div = document.getElementById("canvasDiv");
  let canvas = document.getElementById("canvasPreview");
  div.style.width = el.canvasWidth+ "vw";
  div.style.height = el.canvasHeight + "vw";
  canvas.style.width="100%";
  canvas.style.height="100%";
  div.style.marginLeft = "2.5vw";
  div.style.marginRight = "2.5vw";
  btns.style.width = "50vw";
  btns.style.top = el.canvasHeight + "vw";
  btns.style.opacity = 1;
  canvas.removeEventListener("mouseover",showButtons,false);
	btns.removeEventListener("mouseover",showButtons,false);
	canvas.removeEventListener("mouseout",hideButtons,false);
  setTimeout(function(){
  if(document.getElementById("countdownFromInput")){
    document.getElementById("countdownFromInput").value = el.props.countdownFrom;
    listDevices();
    }},100);
  document.getElementById("container").style.paddingTop = "4vh";
}
function showButtons(){
	var btns= document.getElementById("buttons");
  if(btns){	btns.style.opacity=0.7;}
}

function hideButtons(){
	var btns= document.getElementById("buttons");
  if(btns){btns.style.opacity=0;}
}

const ModalError = (props) => {
  const {
    className
  } = props;

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  return (
    <div>
      <div id='modalBtn' className='button' onClick={toggle}>Error</div>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Reporte de errores</ModalHeader>
        <ModalBody>
					<p>¿Qué pasó?</p>
        	<textarea id='ErrorBox' cols="40" rows="3"></textarea>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={getSysDetails}>Descargar</Button>
          <Button color="secondary" onClick={toggle}>Cerrar</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}
export default SidePanel;
