import './SidePanelWrapper.css'
import './ConfTab.css'
import './ResultsTab.css'
import './DiagnosisTab.css'
import { Spinner,  Button } from 'reactstrap';
import React, {useState} from 'react';
import {listDevices, setGallery2, galleryPrev, galleryNext, removeAllImgBorders} from './functions.js'

import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

import logoDownload from './download.png' 
import logoFlipH from './flipH_2.png'
import logoFlipV from './flipV_2.png'
import logoResetFlip from './reset_image.png'
import logo from './logo.png'
import logoTrash from './trash.png'
import logoTick from './tick.png'
import frame138 from './MaterialDiagnostico/frame138.png'
import frame302 from './MaterialDiagnostico/frame302.png'
import frame1282 from './MaterialDiagnostico/frame1282.png'
import pdf from './sample.pdf'
import iconHidePanel from './hidePanel.png'
import iconShowPanel from './showPanel.png'
import iconError from './error.png'
class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
  setGallery2(){
    setGallery2(this.props);
  }
  componentDidMount(){
    listDevices();
    document.getElementById("countdownFromInput").value="1";
    changeTab(1, this.props.setTabOn, this.props.currentState);
  }
  saveCanvasDim(w,h){
    this.props.saveCanvasDim(w,h);
  }
  getCanvasDim(){
    return this.props.getCanvasDim();
  }

  galleryCtrl(dir, galleryIndxSh, gallerySrc, newPhoto, selectedPhoto){
    if(dir.localeCompare('prev') === 0){
      galleryPrev(galleryIndxSh, gallerySrc, newPhoto, selectedPhoto);
    }
    else if(dir.localeCompare('next') === 0){
      galleryNext(galleryIndxSh, gallerySrc, newPhoto, selectedPhoto);
    }
  }
  render(){
		return(
			<div>
        {!this.props.currentState.hideSidePanel && 
			    <div id="sidePanel_Wrapper">
            <Tabs tabOn={this.props.currentState.tabOn}
                 	currentState={this.props.currentState}
                  setTabOn={this.props.setTabOn}

            />
				    <TabsBody
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		currentState={this.props.currentState}
                    gallerySrc={this.props.gallerySrc}
                    setGalleryIndxShowing={this.props.setGalleryIndxShowing}
                    setSelectedGalleryPhoto={this.props.setSelectedGalleryPhoto}
                    selectedGalleryPhoto = {this.props.currentState.selectedGalleryPhoto}
										superElement={this}
            />
						<ModalError buttonLabel=''/>
          {!this.props.currentState.isVideoLoading  &&  this.props.currentState.renderCanvas &&
            <div id="hideSidePanel"  onClick={()=>hideSidePanel(this)}>
              <img id='iconHidePanel' src={iconHidePanel} alt=''/>
            </div>}
					</div>}

        {this.props.currentState.hideSidePanel && 
          <div id="showSidePanel"  onClick={()=>{showSidePanel(this)}}>
              <img id='iconShowPanel' src={iconShowPanel} alt=''/>
              </div>
        }
			</div>
		);
	}
}

function TabsBody(props) {
  if(props.currentState.tabOn === 1){
    return(
			<ConfigTab superElement={props.superElement} className='tabBody'/>
    );
  }
  else if(props.currentState.tabOn === 2){
    return(
      <GalleryTab gallerySrc={props.gallerySrc} 
                  galleryIndxSh={props.currentState.GalleryIndexShowing}
                  setGalleryIndxShowing={props.setGalleryIndxShowing}
                  setSelectedGalleryPhoto={props.setSelectedGalleryPhoto}
                  selectedPhoto = {props.currentState.selectedGalleryPhoto}
                  downloadHandler = {props.downloadHandler}
                  selectedGalleryPhoto = {props.selectedGalleryPhoto}
                  />
    );
  }
  else if(props.currentState.tabOn === 3){
    return(
      <DiagnosisTab superElement={props.superElement} 
      />
    );
  }
  else if(props.currentState.tabOn === 4){
    return(
      <ReportTab className='tabBody'/>
    );
  }
  else{
    return(
      <div>
      <p>Error</p>
      </div>
    );
  }
}
function Tabs(props){
  return(
    <div id='tabs'>
      <div id='confTab' className='tab' 
           onClick={()=>changeTab(1, props.setTabOn, props.currentState)}>
        <i className="fas fa-cog"></i>
      </div>
      <div id='galleryTab' className='tab'
           onClick={()=>changeTab(2, props.setTabOn, props.currentState)}>
        Resultados de  Examinación  
      </div>
      <div id='resultsTab' className='tab'
           onClick={()=>changeTab(3, props.setTabOn, props.currentState)}>
        Diagnóstico
      </div>
      <div id='reportTab' className='tab'
           onClick={()=>changeTab(4, props.setTabOn, props.currentState)}>
        Informe Médico
      </div>
    </div>
  );
}
function changeTab(n, setTabOn,state){
  if(state.tabsEnabled[n-1]){ 
    setTabOn(n);

  }
}
function ConfigTab(props){
  return(
		<div id="configTab" className='tabBody'>
      <table className = "tableDefault fullSpace">
        <tbody className ="fullSpace">

          <tr>
            <td colSpan="5" style={{}}>
              <div>Seleccione dispositivo:</div>
            </td>
            <td colSpan="5"> 
  		          <select id="videoSelectConf"/>
            </td>
            <td colSpan="2">
  			        <div onClick={()=>{props.superElement.props.setStreamFromConfig()}}
                     id="selectSrcBtn"
                     className='button'
                >Ok</div>
            </td>
            <td colSpan="1">
      	    <span id="RefreshBtn"
                    onClick={()=>{listDevices();}}
                    >
  				    <i className="fas fa-sync-alt"></i>
  			    </span>

            </td>
          </tr>


          <tr style={{height: "5%"}}/>

          <tr style={{height:"2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="13">
              <div>Orientación de la imagen:</div>
            </td>
          </tr>

          <tr style={{height: "2%"}}/>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorHBtn" onClick={()=>{props.superElement.props.mirror("horizontal")}}>
                <img src={logoFlipH} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar horizontalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorVBtn" onClick={()=>{props.superElement.props.mirror("vertical")}}>
                <img src={logoFlipV} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Reflejar verticalmente</div>
            </td>
          </tr>

          <tr>
            <td colSpan="2"/>
            <td colSpan="2">
  				    <div id="mirrorRstBtn" onClick={()=>{props.superElement.props.mirror("reset")}}>
                <img src={logoResetFlip} alt="Video" style={{width: "100%"}}/>
              </div>
            </td>
            <td colSpan="9">
              <div>Restablecer</div>
            </td>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr style={{height: "2px"}}>
            <td colSpan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
          </tr>

          <tr style={{height: "5%"}}/>

          <tr>
            <td colSpan="5">
              <div>Cuenta regresiva desde: </div>
            </td>
            <td colSpan="5">
              <input  type="number" id="countdownFromInput" min="1" max="20"/> 
            </td>
          </tr>

          <tr style={{height: "20%"}}> 
          </tr>
        </tbody>
      </table>
		</div>

  );

}


function DiagnosisTab(props){
	return(
      <div className = "tabBody">
      {!props.superElement.props.currentState.isDiagnosisReady &&

        <table className = "tableDefault fullSpace">
          <tbody className = 'fullSpace'>
            <tr style={{height: "10%"}}/>
            <tr style={{height: "30%"}}>
              <td colSpan="1"/>
              <td colSpan="2">
                <img src={logo} alt="logo" style={{width: "100%"}}/>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "10%"}}>
              <td colSpan="1"/>
              <td colSpan="2" style={{textAlign: "center"}}>
                <div> Procesando... </div>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "10%"}}>
              <td colSpan="1"/>
              <td colSpan="2" style={{textAlign: "center"}}>
  	            <Spinner color="info" style={{margin:'auto'}} size={'xl'}/>
              </td>
              <td colSpan="1"/>
            </tr>
            <tr style={{height: "40%"}}/>
          </tbody>  
        </table>
        }

        {props.superElement.props.currentState.isDiagnosisReady &&
          <div>
            <div id= "diagnosisLeft">
              <img id='diagnosisImg1' src= {frame138} alt="frame" /> 
              <img id='diagnosisImg3' src= {frame1282} alt="frame" />
              <img id= 'diagnosisImg2' src= {frame302} alt="frame" />
              <img id='diagnosisImg4' src= {frame1282} alt="frame" />
              <div id='diagnosisImgTag1'> Tímpano normal </div>
              <div id='diagnosisImgTag2'> Exostosis del conducto </div>
              <div id='diagnosisImgTag3'> Cera </div>
              <div id='diagnosisImgTag4'> Exostosis del conducto </div>
              <div id='pointerImg1' className='donut'/>
              <div id='pointerImg2' className='donut'/>
              <div id='pointerImg3' className='donut'/>
            </div>
            <div id='diagnosisBoxTag1'> Diagnóstico AMIRA:</div>
            <div id='diagnosisBoxTag2'> Diagnóstico final:</div>
            <div id='diagnosisBoxTag3'> Observaciones:</div>
            <div id='diagnosisBox1' className = "AmiraBox"  >
                <ul className = "listNoneItem">
                  <li> Exostosis del conducto </li>
                  <li> Tímpano normal </li>
                  <li> Cera  </li>
               </ul>
            </div>
            <textarea id='diagnosisBox2' className= 'AmiraBox' cols="20" rows="1"></textarea>
            <textarea id='diagnosisBox3' className= 'AmiraBox' cols="20" rows="1"></textarea>
            <div id='btnMakeReport'>
              Generar Informe
            </div>
            </div>
        /*
        <table className = "tableDefault fullSpace" style={{zIndex: 3}}>
          <tbody>
            <tr className = "fullSpace">

              <td colSpan="3">

                <table id='diagnosisLeft' class = "tableDefault fullSpace" style={{fontSize: '90%'}}>
                  <tbody>
                    <tr style={{height: '2%'}}/>

                    <tr style={{height: '20%'}}>
                      <td colSpan="1">
                        <img src= {frame138} alt="frame" style={{width: "90%"}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Tímpano normal </div>
                      </td>
                    </tr>

                    <tr style={{height: '10%'}}/>

                    <tr style={{height: '20%'}}>
                      <td colSpan="1">
                        <img src= {frame302} alt="frame" style={{width: "90%", zIndex: '3'}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Exostosis del conducto </div>
                      </td>
                    </tr>

                    <tr style={{height: '10%'}}/>

