                      <td colSpan="1">
                        <img src= {frame1282} alt="frame" style={{width: "90%"}}/>
                      </td>
                      <td colSpan="1">
                        <div style={{padding: "0.5vh"}}> Cera </div>
                      </td>
                    </tr>

                    <tr style={{height: '18%'}}/>
                  </tbody>
