import './SidePanel.css'
import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Card,CardImg, Button,  CardText, Row, Col, Container} from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
import {listDevices} from './functions.js'

import logoVideo from './logoVideo.png'
import logoImg from './logoImg.png'
import logoFlipH from './flipH.png'
import logoFlipV from './flipV.png'
import logo from './logo.png'
import frame138 from './MaterialDiagnostico/frame138.png'
class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
    this.canvasHeight = 0;
    this.canvasWidth = 0;
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
  componentDidMount(){
    listDevices();
    //document.getElementById("countdownFromInput").value="1";
  }
  saveCanvasDim(w,h){
    this.props.saveCanvasDim(w,h);
  }
  getCanvasDim(){
    return this.props.getCanvasDim();
  }
  render(){
		return(
			<div>
        {!this.props.currentState.hideSidePanel && 
			    <div id="sidePanel_Wrapper">
				    <SideTabs
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		screenshots={this.props.currentState.GallerySrc}
                 		currentState={this.props.currentState}
										superElement={this}
            />
          {!this.props.currentState.isVideoLoading  &&  this.props.currentState.renderCanvas &&
            <div id="hideSidePanel" className="ctrlTabs rotate90" onClick={()=>hideSidePanel(this)}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
            </div>}
					</div>}

        {this.props.currentState.hideSidePanel && 
          <div id="showSidePanel" className="ctrlTabs rotate-90" onClick={()=>{showSidePanel(this);console.log(document.getElementById("countdownFromInput"))}}>
              <i className="fas fa-angle-up sidePanelArrow"></i>
              </div>
        }
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    screenshots,
    currentState,
		superElement,
  } = props;
const [activeTab, setActiveTab] = useState(superElement.props.currentState.tabOn);

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
      superElement.props.setTabOn(tab);
  }

  if(activeTab !== superElement.props.currentState.tabOn){
    setActiveTab(superElement.props.currentState.tabOn);
  }

  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '1' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('1'); }}>
            <i class="fas fa-cog"></i>
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('2'); }}>
            Capturas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('3'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '4' })}
            onClick={() => { toggle('4'); }} >
            Diagnostico
          </NavLink>
        </NavItem>
      </Nav>
      <div id="TabsBody">
      <TabContent activeTab={activeTab} style={{height: "100%"}}>
        <TabPane tabId="1" style={{height: "100%"}}>
					  <ConfigTab superElement={superElement} style={{height: "100%"}}/>
        </TabPane>
        <TabPane tabId="2">
          <GalleryTab screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="3">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler} screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="4">
            <DiagnosisTab />
        </TabPane>
      </TabContent>
      </div>
    </div>
  );
}
function GalleryTab(props){
  let  screenshotsIsEmpty = (props.screenshots.length === 0)? true:false;
  return(
    <div>
      {screenshotsIsEmpty && <p> No se encuentran capturas disponibeles.  </p>}
      <Gallery images={props.screenshots} rowHeight="80"/>
    </div>

  );
}
function ConfigTab(props){
  return(
    /*
		<div id="configTab">
    <table class = "tableDefault fullSpace">

        <tr style={{height: "5%"}}/>

        <tr>
          <td colspan="5" style={{ position: "relative"}}>
            <div>Seleccione dispositivo:</div>
          </td>
          <td colspan="5"> 
		          <select id="videoSelectConf"/>
          </td>
          <td colspan="2">
			        <Button color='secondary'
                      outline
                      onClick={()=>{props.superElement.props.setStreamFromConfig()}}
                      id="selectSrcBtn"
              >Ok</Button>
          </td>
          <td colspan="1">
    	    <span id="RefreshBtn"
                  onClick={()=>{listDevices();}}
                  >
				    <i class="fas fa-sync-alt"></i>
			    </span>

          </td>
        </tr>


        <tr style={{height: "5%"}}/>

        <tr style={{height:"2px"}}>
          <td colspan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
        </tr>

        <tr style={{height: "5%"}}/>

        <tr>
          <td colspan="13">
            <div>Orientación de la imagen:</div>
          </td>
        </tr>

        <tr style={{height: "2%"}}/>

        <tr>
          <td colspan="2"/>
          <td colspan="2">
				    <div id="mirrorHBtn" onClick={()=>{props.superElement.props.mirror("horizontal")}}>
              <img src={logoFlipH} alt="Video" style={{width: "100%"}}/>
            </div>
          </td>
          <td colspan="9">
            <div>Reflejar horizontalmente</div>
          </td>
        </tr>

        <tr>
          <td colspan="2"/>
          <td colspan="2">
				    <div id="mirrorVBtn" onClick={()=>{props.superElement.props.mirror("vertical")}}>
              <img src={logoFlipV} alt="Video" style={{width: "100%"}}/>
            </div>
          </td>
          <td colspan="9">
            <div>Reflejar verticalmente</div>
          </td>
        </tr>

        <tr>
          <td colspan="2"/>
          <td colspan="2">
				    <div id="mirrorRstBtn" onClick={()=>{props.superElement.props.mirror("reset")}}>
              <img src={logoFlipH} alt="Video" style={{width: "100%"}}/>
            </div>
          </td>
          <td colspan="9">
            <div>Restablecer</div>
          </td>
        </tr>

        <tr style={{height: "5%"}}/>

        <tr style={{height: "2px"}}>
          <td colspan="13" style={{backgroundColor: "rgba(0,0,0,0.3)"}}/>
        </tr>

        <tr style={{height: "5%"}}/>

        <tr>
          <td colspan="5">
            <div>Cuenta regresiva desde: </div>
          </td>
          <td colspan="5">
            <input  type="number" id="countdownFromInput" min="1" max="20"/> 
          </td>
        </tr>

        <tr style={{height: "15%"}}> 
        </tr>
      </table>
		</div>
    */
    <DiagnosisTab/>
  );

}

function MirrorIcon(){
	return(
		<svg width="100%" height="100%" viewBox="0 0 16 16" class="bi bi-hr" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
			<path fill-rule="evenodd" d="M0 8a.5.5 0 0 1 .5-.5h15a.5.5 0 0 1 0 1H.5A.5.5 0 0 1 0 8z"/>
			<path d="M4 3h8a1 1 0 0 1 1 1v2.5h1V4a2 2 0 0 0-2-2H4a2 2 0 0 0-2 2v2.5h1V4a1 1 0 0 1 1-1zM3 9.5H2V12a2 2 0 0 0 2 2h8a2 2 0 0 0 2-2V9.5h-1V12a1 1 0 0 1-1 1H4a1 1 0 0 1-1-1V9.5z"/>
