	);
}
function DiagnosisTab(){
	return(
      <div class = "fullSpace">
        {false &&
         <table class = "tableDefault fullSpace">
           <tr style={{height: "20%"}}/>
           <tr style={{height: "30%"}}>
             <td colspan="1"/>
