           </tr>
           <tr style={{height: "10%"}}>
             <td colspan="1"/>
             <td colspan="1" style={{textAlign: "center"}}>
               <div> Procesando... </div>
             </td>
             <td colspan="1"/>
           </tr>
           <tr style={{height: "10%"}}>
             <td colspan="1"/>
             <td colspan="1" style={{textAlign: "center"}}>
  	           <Spinner color="info" style={{position:'relative',margin:'auto'}} size={'xl'}/>
             </td>
             <td colspan="1"/>
           </tr>
           <tr style={{height: "30%"}}/>
         </table>
         }

         {true &&
         <table class = "tableDefault fullSpace">
          <tr class= "fullSpace">

            <td colspan="3">
              <table class = "tableDefault fullSpace">
                <tr>
                  <td colspan="1">
                    <img src= {frame138} alt="frame" style={{width: "100%"}}/>
                  </td>
                  <td colspan="1">
                    <div style={{padding: "0.5vh"}}> Tímpano normal </div>
                  </td>
                </tr>
              </table>
            </td>

            <td colspan="2">
              <table class = "tableDefault fullSpace">

                <tr style = {{height: "30%", position: 'realtive' }}>
                  <div style={{fontSize: '90%'}} > Diagnóstico AMIRA:</div>
                  <div class = "AmiraBox" style={{fontSize: '90%'}} >
                  <textarea cols="20" rows="4" style={{height: "100%", width: '100%', position: 'relative',  resize: 'none'}} >
                      ajsa
                      sa
                      asd
                    </textarea>
                      <ul class="listNoneItem">
                        <li> Exostosis del conducto </li>
                        <li> Tímpano normal </li>
                        <li> Cera  </li>
                     </ul>
