import './SidePanel.css'
import { Spinner,TabContent, TabPane, Nav, NavItem, NavLink, Card,CardImg, Button,  CardText, Row, Col, Container} from 'reactstrap';
import React, {useState} from 'react';
import classnames from 'classnames';
import Gallery from 'react-grid-gallery';
import {listDevices} from './functions.js'

class SidePanel extends React.Component{
	constructor(props){
		super(props);
		this.state = {
		}
		this.selectVideoSrc = React.createRef();
	}
  downloadHandler(media){
    this.props.downloadHandler(media);
  }

  listDevices(){
    listDevices();
  }
	render(){
		return(
			<div id="sidePanel_Wrapper">
					<SideTabs
                 		thumbnail={this.props.thumbnail} 
                 		downloadHandler={(media)=> this.downloadHandler(media)}
                 		screenshots={this.props.currentState.GallerySrc}
                 		currentState={this.props.currentState}
										superElement={this}

                 />
			</div>
		);
	}
}

const SideTabs = (props) => {
  const {
    thumbnail,
    downloadHandler,
    screenshots,
    currentState,
		superElement,
  } = props;
  const [activeTab, setActiveTab] = useState('4');

  const toggle = tab => {
    if(activeTab !== tab) setActiveTab(tab);
  }


  return (
    <div>
      <Nav tabs>
        <NavItem>
          <NavLink
            disabled = {!currentState.enableDiagnosisTab}
            className={classnames({ active: activeTab === '1' })}
            onClick={() => { toggle('1'); }} >
            Diagnostico
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '2' })}
            disabled = {!currentState.enableDownloadsTab}
            onClick={() => { toggle('2'); }}>
            Descargas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '3' })}
            disabled = {!currentState.enableGalleryTab}
            onClick={() => { toggle('3'); }}>
            Capturas
          </NavLink>
        </NavItem>
        <NavItem>
          <NavLink
            className={classnames({ active: activeTab === '4' })}
            disabled = {!currentState.enableConfigTab}
            onClick={() => { toggle('4'); }}>
           Configuración 
          </NavLink>
        </NavItem>
      </Nav>
      <div id="TabsBody">
      <TabContent activeTab={activeTab}>
        <TabPane tabId="1">
          <Row>
            <Col sm="12">
              <DiagnosisTab />
            </Col>
          </Row>
        </TabPane>
        <TabPane tabId="2">
					<DownloadTab thumbnail={thumbnail} downloadHandler={downloadHandler} screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="3">
          <GalleryTab screenshots={screenshots}/>
        </TabPane>
        <TabPane tabId="4">
					<ConfigTab superElement={superElement} />
        </TabPane>
      </TabContent>
      </div>
    </div>
  );
}
function GalleryTab(props){
  return(
    <Gallery images={props.screenshots} rowHeight="80"/>
  );
}
function ConfigTab(props){
  return(
		<div id="videoSelectDiv">
    	<Button color ='primary'
              onClick={()=>{listDevices();}}
    		      >Refrescar</Button>
		  <select ref={props.superElement.selectVideoSrc}
							style={{width: 300}}
							id="videoSelectConf"/>
			<Button color='primary'
              onClick={()=>{props.superElement.props.setStreamFromConfig()}}
      >Ok</Button>
      <Button onClick={()=>{props.superElement.props.mirror("horizontal")}}> MH </Button>
      <Button onClick={()=>{props.superElement.props.mirror("vertical")}}> MV </Button>
		</div>
  );
}


function DiagnosisTab(){
	return(
		<Spinner color="info" style={{position:'absolute',margin:'auto'}} size={'xl'}/>
	);
}

const DownloadTab = (props) => {
  const{
    thumbnail,
    downloadHandler,
    screenshots
  }=props;

  let  screenshotsIsEmpty = (screenshots.length === 0)? true:false;
	return(
		<Container>
			<Row>
      	<Col sm="6">
          <Card body>
            <CardImg top width="10%" src= {thumbnail} alt="thumbnail" />
            <CardText>Grabación del examen.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("video")}}> Descargar</Button>
          </Card>
        </Col>
        {!screenshotsIsEmpty &&
        <Col sm="6">
          <Card body>
            <CardImg top width="100%" src= {screenshots[0].src} alt="thumbnail" />
            <CardText>Capturas de Pantalla.</CardText>
            <Button color='primary' onClick={()=>{downloadHandler("screenshots")}}>Descargar</Button>
          </Card>
        </Col>
        }
     </Row>
		</Container>
	);
}
export default SidePanel;
