import JSZipUtils from 'jszip-utils';
import JSZip from 'jszip';
import FileSaver from 'file-saver';

export var handleDataAvailable = function(el) {
  return function (event) {
    console.log('handleDataAvailable');
      if (event.data && event.data.size > 0) {
      el.recordedChunks.push(event.data);
      var blob = new Blob(el.recordedChunks, {
        type: "video/webm"
      });
      el.videoRecorded = URL.createObjectURL(blob);
      var videoElement = document.getElementById("recordedVideoElement");
      videoElement.src = null;
      videoElement.srcObject = null;
      videoElement.src = el.videoRecorded;
      }
        else {
        console.log("no data available for recording");
      }
  }
}

  export function setProgressBarColor(ctxt){
    let p =parseInt(ctxt.props.currentState.progress,10);
    if (p < 80){
      ctxt.progressBarColor = "info";
    }
    else if (p < 100){
      ctxt.progressBarColor = "success";
    }
    else{
      ctxt.progressBarColor = "danger";
    }
    return ctxt.progressBarColor;
  }

  export function ctrlBtnFunction(ctxt){
    switch(ctxt.props.currentState.stateName){
      case 'Idle':
        ctxt.props.stateHandler('GetCon');
        // Dummy, despues vendra del servidor.
        setTimeout(()=>{ctxt.props.stateHandler('Countdown');ctxt.startRecording();ctxt.timeStartPressed = Date.now();}, 1000);
        // Enviar al servidor la solicitud de conexión.
        break;
      case 'GetCon':
        ctxt.props.stateHandler('Idle');
        break;
      case 'Examination':
        ctxt.props.stateHandler('Review');
        ctxt.stopRecording();
        // Enviar al servidor la solicitud de diagnóstico.
        break;
      case 'Review':
        ctxt.props.stateHandler('Idle');
          break;
      default:
        console.log("Error: Boton apretado en estado que no correspondía.")
    }
  }

  export function countdown(canvas,el){
    let context = canvas;	
    let countdown = el.props.countdownFrom- Math.trunc((-el.timeStartPressed + Date.now())/1000);
    context.beginPath();
    context.arc(320, 240, 130, 0, 2 * Math.PI);
    context.fillStyle = "rgba(255, 255, 255, 0.8)";;
    context.fill();
    context.beginPath();
    context.fillStyle = "black";
    if(countdown >= 10){
      context.fillText(countdown.toString(10),200,315);
    }else if(countdown >= 1){
      context.fillText(countdown.toString(10),270,308,100);
    }

    context.fill();
    if (countdown <  1){
          el.props.stateHandler("Examination");
    }
  }

  export function urlToPromise(url) {
    return new Promise(function(resolve, reject) {
      JSZipUtils.getBinaryContent(url, function (err, data) {
        if(err) {
          reject(err);
        }
        else {
          resolve(data);
        }
      });
    });
  }

  export function downloadScreenshots(el){
    let zip = new JSZip();
    for(let i=0;i < el.props.currentState.GallerySrc.length; i++){
      let name = "Screenshot_" + i + ".jpeg";
      zip.file(name,urlToPromise(el.props.currentState.GallerySrc[i].src),{binary:true});
    }
    zip.generateAsync({type: "blob"}).then(function(content) {
      FileSaver.saveAs(content, "download.zip");
    });
  }

  export function downloadVideo(el){
    var aux = document.createElement("a");
    document.body.appendChild(aux);
    aux.style = "display: none";
    aux.href = el.videoRecorded;
    aux.download = "test.webm";
    document.body.appendChild(aux);
    aux.click();		
  }

  export function captureScreenshot(el){
    var canvas = document.querySelector("#canvasPreview");
    var imgAsDataURL = canvas.toDataURL("image/jpeg",1);
    let newSlidesData = [{src: imgAsDataURL, 
                          thumbnail: imgAsDataURL,
                          thumbnailWidth: 160,
                          thumbnailHeight: 120
                        }];
    el.props.setScreenshots(newSlidesData);
    }

export function mirror(el,way){
  console.log("aa");
	if(way.localeCompare("horizontal") === 0){
    el.mirrorTransormTranslate[0] += el.mirrorTransformTranslate[0]*el.mirrorTransform
    el.mirrorTransform[0]*=-1;
  }
  else if(way.localeCompare("vertical") === 0){
    el.mirrorTransform[1]*=-1;
  }
}



  export default {handleDataAvailable, setProgressBarColor, ctrlBtnFunction, countdown, urlToPromise,
                 downloadScreenshots, downloadVideo, captureScreenshot, mirror}
