function manageStates(state){
  var flags = {};
  switch(state){
  	case 'OnConnection':
    	flags={
				textMainBtn: "Start",
				showStartBtn: true,
				showCaptureBtn: true,
				showVideoSelect: false,
				showProgressBar: false,
				showModalScreenshots: true, //if requested
				showModalDiagnosis: false,
				disableBtnCapture: false,

				streaming: false,
				almostStreaming: false,          
			};
			break;
		case 'Countdown':
			flags={
				textMainBtn: "Stop",
				showStartBtn: true,
				showCaptureBtn: true,
				showVideoSelect: false,
				showProgressBar: true,
				showModalScreenshots: false, //if requested
				showModalDiagnosis: false,
				disableBtnCapture: true,

				streaming: false,
				almostStreaming: true,          

				timeStartPressed: Date.now()
				};
				break;
		case 'Streaming':
			flags={
				textMainBtn: "Stop",
				showStartBtn: true,
				showCaptureBtn: true,
				showVideoSelect: false,
				showProgressBar: true,
				showModalScreenshots: false, //if requested
				showModalDiagnosis: false,
				disableBtnCapture: false,

				streaming: true,
				almostStreaming: false,          

			};
			break;
		case 'StopStream':
			flags={
				textMainBtn: "Start",
				showStartBtn: true,
				showCaptureBtn: true,
				showVideoSelect: false,
				showProgressBar: true,
				showModalScreenshots: false, //if requested
				showModalDiagnosis: false,
				disableBtnCapture: true,

				streaming: false,
				almostStreaming: true,          
			};
			break;
		case 'closeDiagnosis':
			flags={
				showModalDiagnosis: false
			};	
			break;
		default:
			console.log('State not defined');

	}
  return flags;
}
